						, tpl_hex_rango_fecha_fin
						, tpl_asi_rango_fechas_ingreso
						, tpl_permite_trans_luego_corte
						, tpl_ignora_aut_luego_corte
						, tpl_genera_proximo_periodo
						, tpl_property_bag_data
						, tpl_usuario_grabacion
					) VALUES (
						@codcia
						, src.tpl_codigo_visual
						, src.tpl_codmon
						, src.tpl_descripcion
						, src.tpl_frecuencia
						, src.tpl_aplicacion 
						, src.tpl_num_periodo 
						, src.tpl_total_periodos
						, src.tpl_hex_rango_fechas_ingreso 
						, src.tpl_hex_offset_rango_fechas
						, src.tpl_hex_override_fecha_fin
						, src.tpl_hex_rango_fecha_fin
