WITH src AS (
SELECT 
	xt.xc.value('@rsa_descripcion', 'varchar(250)') AS rsa_descripcion
	, xt.xc.value('@rsa_es_salario_base', 'bit') AS rsa_es_salario_base
	, xt.xc.value('@rsa_usuario_grabacion', 'varchar(50)') AS rsa_usuario_grabacion
 FROM @rsaXML.nodes('/rubros/rubro') AS xt(xc)
) MERGE INTO exp.rsa_rubros_salariales dest
	USING src ON src.rsa_descripcion = dest.rsa_descripcion AND src.rsa_es_salario_base = dest.rsa_es_salario_base AND dest.rsa_codcia = @codcia
		WHEN NOT MATCHED THEN
			INSERT (	rsa_codcia
						, rsa_descripcion
						, rsa_es_salario_base
						, rsa_usuario_grabacion
					) VALUES (
						@codcia
						, src.rsa_descripcion
						, src.rsa_es_salario_base  
						, src.rsa_usuario_grabacion
					);
