  <lista vli_id="CenTransSinPermisoDeRolEnviado" vli_description="Entidades que no han sido asignadas como permitidas para el rol enviado" vli_prompt="Entidad" vli_value_source="UseItemData" vli_codfld="string" vli_enable_caching="0" vli_caching_expiration="0" vli_caching_expiration_mins="0" vli_definition="&lt;List&gt;&lt;Bound&gt;select cen_nombre, isnull(cen_descripcion, cen_nombre) descripcion from cfg.cen_cfg_entidades where not exists(select top 1 * from sec.pte_per_trans_entidad_rol where pte_codcen = cen_nombre and pte_codrol = ''@codrol'') order by 2&lt;/Bound&gt;&lt;/List&gt;" vli_usuario_grabacion="EVA" />
  <lista vli_id="CentroCostoOmEx" vli_description="Centros de costoso que se mostraran en el omniexpediente" vli_value_source="UseItemData" vli_codfld="string" vli_enable_caching="0" vli_caching_expiration="0" vli_caching_expiration_mins="0" vli_definition="&lt;List&gt;&lt;Bound&gt;&#xA;  select cco_nomenclatura_contable, cco_descripcion from eor.cco_centros_de_costo where cco_codcia = $$CODCIA$$ and cco_estado = ''Vigente''&#xA; order by cco_descripcion&#xA;&lt;/Bound&gt;&lt;/List&gt;" vli_usuario_grabacion="EVA" />
