  <lista vli_id="CentrosCostosDeEmpleoEnviado" vli_description="Lista los centros de costo que estas asociados a la plaza del empleado que se envia como parametro" vli_prompt="CentroCosto" vli_value_source="UseItemData" vli_codfld="int" vli_enable_caching="0" vli_caching_expiration="0" vli_caching_expiration_mins="0" vli_definition="&lt;List&gt;&lt;Bound&gt;&#xA;    select cco_codigo, cco_descripcion from eor.cpp_centros_costo_plaza&#xA; join eor.cco_centros_de_costo on cco_codigo = cpp_codcco&#xA; join eor.plz_plazas on plz_codigo = cpp_codplz&#xA; join exp.emp_empleos on emp_codplz = plz_codigo&#xA; where emp_codigo = @codemp and cco_estado = ''Vigente''&#xA;  &lt;/Bound&gt;&lt;/List&gt;" vli_usuario_grabacion="EVA" />
  <lista vli_id="CentrosCostosDeGrupoEnviado" vli_description="Lista de valores de los centros de costo por gurpo corporativo del empleado solicitante" vli_value_source="UseItemData" vli_codfld="int" vli_enable_caching="0" vli_caching_expiration="0" vli_caching_expiration_mins="0" vli_definition="&lt;List&gt;&lt;Bound&gt;&#xA;select cco_codigo, ''('' + cia_descripcion + '') - '' + cco_descripcion cco_descripcion&#xA;  from eor.cco_centros_de_costo&#xA;  join eor.cia_companias on cia_codigo = cco_codcia&#xA;  WHERE cia_codgrc = @codgrc and cco_estado = ''Vigente''&#xA; order by cia_descripcion, cco_descripcion&lt;/Bound&gt;&lt;/List&gt;" vli_usuario_grabacion="EVA" />
  <lista vli_id="CentrosCostosPlazaDeEmpleoEnviado" vli_description="Lista de centros de costo de la plaza del empleado solicitante" vli_value_source="UseItemData" vli_codfld="int" vli_enable_caching="0" vli_caching_expiration="0" vli_caching_expiration_mins="0" vli_definition="&lt;List&gt;&lt;Bound&gt;&#xA;select cco_codigo, cco_descripcion, cpp_porcentaje &#xA;  from exp.emp_empleos &#xA;  join eor.cpp_centros_costo_plaza on cpp_codplz = emp_codplz&#xA;  join eor.cco_centros_de_costo on cco_codigo = cpp_codcco and cco_estado = ''Vigente''&#xA;where emp_codigo = @codemp&lt;/Bound&gt;&lt;/List&gt;" vli_usuario_grabacion="EVA" />
  <lista vli_id="CentrosDeCostoAutorizadosDeGrupoEnviado" vli_description="Lista los centros de costos autorizados para el usuario autenticado y del grupo corporativo enviado como parametro" vli_value_source="UseItemData" vli_codfld="int" vli_enable_caching="0" vli_caching_expiration="0" vli_caching_expiration_mins="0" vli_definition="&lt;List&gt;&lt;Bound&gt;&#xA;    select cco_codigo, cco_descripcion + '' ('' + cia_descripcion + '')'' cco_descripcion from eor.cco_centros_de_costo join eor.cia_companias on cia_codigo = cco_codcia where exists (select null from sco.permiso_compania_tabla(''$$USER$$'') where cco_codcia = codcia) and cia_codgrc = @codgrc and cco_estado = ''Vigente'' order by cco_descripcion&#xA;  &lt;/Bound&gt;&lt;/List&gt;" vli_usuario_grabacion="EVA" />
