  <lista vli_id="CentrosDeTrabajoComoSegmentacion" vli_description="Listado de centros de trabajo como segmentacion" vli_value_source="UseSqlExpressionInItemData" vli_codfld="string" vli_enable_caching="0" vli_caching_expiration="0" vli_caching_expiration_mins="0" vli_definition="&lt;List&gt;&lt;Bound&gt;&#xA;SELECT ''select exp_codigo &#xA;from exp.exp_expedientes&#xA;  join exp.emp_empleos on emp_codexp = exp_codigo&#xA;  join eor.plz_plazas on plz_codigo = emp_codplz&#xA;where plz_codcdt = '' + cast(cdt_codigo as varchar), cdt_descripcion&#xA;FROM eor.cdt_centros_de_trabajo &#xA;&lt;/Bound&gt;&lt;/List&gt;" vli_usuario_grabacion="EVA" />
