  <lista vli_id="CentrosDeTrabajoPermitidasDeCompaniaEnviada" vli_description="Lista los centros de trabajo permitidos de la compañía enviada" vli_value_source="UseItemData" vli_codfld="int" vli_enable_caching="0" vli_caching_expiration="0" vli_caching_expiration_mins="0" vli_definition="&lt;List&gt;&lt;Bound&gt;&#xA;    select cdt_codigo, cdt_descripcion from eor.cdt_centros_de_trabajo where cdt_codcia = @codcia and exists (select null from sco.permiso_compania_tabla(''$$USER$$'') where cdt_codcia = codcia)&#xA;  &lt;/Bound&gt;&lt;/List&gt;" vli_usuario_grabacion="EVA" />
  <lista vli_id="CentrosDeTrabajoPptoDeCompaniaSeleccionada" vli_description="Lista los centros de trabajo en el presupuesto de la compañia seleccionada" vli_value_source="UseSqlExpressionInItemData" vli_codfld="int" vli_enable_caching="0" vli_caching_expiration="0" vli_caching_expiration_mins="0" vli_definition="&lt;List&gt;&lt;Bound&gt;&#xA;    select ctp_codigo, ctp_descripcion from pto.ctp_centros_de_trabajo where ctp_codcia = $$CODCIA$$&#xA;&lt;/Bound&gt;&lt;/List&gt;" vli_usuario_grabacion="EVA" />
  <lista vli_id="CentrosTrabajoDeEmpleosActivos" vli_description="Muestra los centros de trabajo de los empleados activos" vli_value_source="UseItemData" vli_codfld="int" vli_enable_caching="0" vli_caching_expiration="0" vli_caching_expiration_mins="0" vli_definition="&lt;List&gt;&lt;Bound&gt;&#xA;    select cdt_codigo, cdt_descripcion&#xA;from eor.cdt_centros_de_trabajo&#xA;join eor.cia_companias on cia_codigo = cdt_codcia&#xA;where exists(&#xA;select null         &#xA;  from sec.eus_expediente_usuario&#xA;  join sec.usr_users on usr_codigo = eus_codusr&#xA;  join exp.emp_empleos on emp_codexp = eus_codexp&#xA;  join eor.plz_plazas on plz_codigo = emp_codplz&#xA;  join eor.pue_puestos on pue_codigo = plz_codpue&#xA;where usr_username = ''$$USER$$'' and emp_estado = ''A''&#xA;       and cia_codgrc = pue_codgrc)&#xA;  &lt;/Bound&gt;&lt;/List&gt;" vli_usuario_grabacion="EVA" />
