  <lista vli_id="CentrosTrabajoDePlazasVacantes" vli_description="Muestra los centros de trabajo de las plazas vacantes" vli_value_source="UseItemData" vli_codfld="int" vli_enable_caching="0" vli_caching_expiration="0" vli_caching_expiration_mins="0" vli_definition="&lt;List&gt;&lt;Bound&gt;&#xA;    select cdt_codigo, cdt_descripcion&#xA;from eor.cdt_centros_de_trabajo&#xA;join eor.cia_companias on cia_codigo = cdt_codcia&#xA;where exists(&#xA;select null         &#xA;  from sec.eus_expediente_usuario&#xA;  join sec.usr_users on usr_codigo = eus_codusr&#xA;  join exp.emp_empleos on emp_codexp = eus_codexp&#xA;  join eor.plz_plazas on plz_codigo = emp_codplz&#xA;  join eor.pue_puestos on pue_codigo = plz_codpue&#xA;where usr_username = ''$$USER$$'' and emp_estado = ''A''&#xA;       and cia_codgrc = pue_codgrc)&#xA;  &lt;/Bound&gt;&lt;/List&gt;" vli_usuario_grabacion="EVA" />
