  <lista vli_id="EdadesComoSegmentacionClima" vli_description="Listado de edades de las encuestas de clima" vli_value_source="UseSqlExpressionInItemData" vli_codfld="string" vli_enable_caching="0" vli_caching_expiration="0" vli_caching_expiration_mins="0" vli_definition="&lt;List&gt;&lt;Unbound&gt;&lt;Item&gt;&lt;ID&gt;select exp_codigo from exp.exp_expedientes where exp_edad &amp;lt; 18&lt;/ID&gt;&lt;Description&gt;Menos de 18&lt;/Description&gt;&lt;/Item&gt;&lt;Item&gt;&lt;ID&gt;select exp_codigo from exp.exp_expedientes where exp_edad between 18 and 25&lt;/ID&gt;&lt;Description&gt;De 18 a 25&lt;/Description&gt;&lt;/Item&gt;&lt;Item&gt;&lt;ID&gt;select exp_codigo from exp.exp_expedientes where exp_edad between 26 and 35&lt;/ID&gt;&lt;Description&gt;De 26 a 35&lt;/Description&gt;&lt;/Item&gt;&lt;Item&gt;&lt;ID&gt;select exp_codigo from exp.exp_expedientes where exp_edad between 36 and 45&lt;/ID&gt;&lt;Description&gt;De 36 a 45&lt;/Description&gt;&lt;/Item&gt;&lt;Item&gt;&lt;ID&gt;select exp_codigo from exp.exp_expedientes where exp_edad between 46 and 55&lt;/ID&gt;&lt;Description&gt;De 46 a 55&lt;/Description&gt;&lt;/Item&gt;&lt;Item&gt;&lt;ID&gt;select exp_codigo from exp.exp_expedientes where exp_edad &amp;gt; 55&lt;/ID&gt;&lt;Description&gt;Más de 55&lt;/Description&gt;&lt;/Item&gt;&lt;/Unbound&gt;&lt;/List&gt;" vli_usuario_grabacion="EVA" />
  <lista vli_id="EdadesEncuestasClima" vli_description="Listado de edades de las encuestas de clima" vli_value_source="UseItemData" vli_codfld="int" vli_enable_caching="0" vli_caching_expiration="0" vli_caching_expiration_mins="0" vli_definition="&lt;List&gt;&lt;Unbound&gt;&lt;Item&gt;&lt;ID&gt;Menos de 18&lt;/ID&gt;&lt;Description&gt;Menos de 18&lt;/Description&gt;&lt;/Item&gt;&lt;Item&gt;&lt;ID&gt;De 18 a 25&lt;/ID&gt;&lt;Description&gt;De 18 a 25&lt;/Description&gt;&lt;/Item&gt;&lt;Item&gt;&lt;ID&gt;De 26 a 35&lt;/ID&gt;&lt;Description&gt;De 26 a 35&lt;/Description&gt;&lt;/Item&gt;&lt;Item&gt;&lt;ID&gt;De 36 a 45&lt;/ID&gt;&lt;Description&gt;De 36 a 45&lt;/Description&gt;&lt;/Item&gt;&lt;Item&gt;&lt;ID&gt;De 46 a 55&lt;/ID&gt;&lt;Description&gt;De 46 a 55&lt;/Description&gt;&lt;/Item&gt;&lt;Item&gt;&lt;ID&gt;Mas de 55&lt;/ID&gt;&lt;Description&gt;Más de 55&lt;/Description&gt;&lt;/Item&gt;&lt;/Unbound&gt;&lt;/List&gt;" vli_usuario_grabacion="EVA" />
  <lista vli_id="ElementosPorCompania" vli_description="Todos los elementos por compañía" vli_prompt="Elemento" vli_value_source="UseSqlExpressionInItemData" vli_codfld="int" vli_enable_caching="0" vli_caching_expiration="0" vli_caching_expiration_mins="0" vli_definition="&lt;List&gt;&lt;Bound&gt;&#xA;SELECT ele_codigo, ele_descripcion&#xA;FROM sso.ele_elementos&#xA;WHERE ele_codcia = $$CODCIA$$&#xA;  AND exists (select null from sco.permiso_compania_tabla(''$$USER$$'') where ele_codcia = codcia)&#xA;  AND ele_codtie = @codtie&#xA;  AND NOT EXISTS (SELECT NULL&#xA;          FROM sso.epr_elementos_procesos&#xA;          WHERE epr_codele = ele_codigo&#xA;            AND epr_codpro = @codpro) &#xA;  &lt;/Bound&gt;&lt;/List&gt;" vli_usuario_grabacion="EVA" />
  <lista vli_id="EmpActivosYCompaniaDeExpedienteEnviadoNoCodVisual" vli_description="Lista los empleos activos de un expediente enviado" vli_prompt="Empleo" vli_value_source="UseItemData" vli_codfld="int" vli_enable_caching="1" vli_caching_expiration="2" vli_caching_expiration_mins="1" vli_definition="&lt;List&gt;&lt;Bound&gt;&#xA;    select emp_codigo, pue_nombre from exp.emp_empleos&#xA;join eor.plz_plazas on plz_codigo = emp_codplz&#xA;join eor.pue_puestos on pue_codigo = plz_codpue&#xA;where emp_codexp = @codexp and emp_estado = ''A''&#xA;  &lt;/Bound&gt;&lt;/List&gt;" vli_usuario_grabacion="EVA" />
  <lista vli_id="EmpleadoEstadosActivos" vli_description="Listado de los posibles estados que puede tener un empleado cuando esta activo" vli_value_source="UseItemData" vli_codfld="string" vli_enable_caching="0" vli_caching_expiration="0" vli_caching_expiration_mins="0" vli_definition="&lt;List&gt;&lt;Unbound&gt;&lt;Item&gt;&lt;ID&gt;A&lt;/ID&gt;&lt;Description&gt;{Activo, Empleado}&lt;/Description&gt;&lt;/Item&gt;&lt;Item&gt;&lt;ID&gt;S&lt;/ID&gt;&lt;Description&gt;{Suspendido, Empleado}&lt;/Description&gt;&lt;/Item&gt;&lt;/Unbound&gt;&lt;/List&gt;" vli_usuario_grabacion="EVA" />
  <lista vli_id="EmpleadosAsignacionJornadaSeguridadContenido" vli_description="Listado para los empleados permitidos en las asignaciones de jornada por seguridad de Contenido (El primer elemento debe de ser el código de empleado)" vli_value_source="UseItemData" vli_codfld="int" vli_enable_caching="0" vli_caching_expiration="0" vli_caching_expiration_mins="0" vli_definition="&lt;List&gt;&lt;Bound&gt;&#xA;    select emp_codigo, (case emp_estado when ''A'' then ''Activo'' when ''R'' then ''Retirado'' when ''N'' then ''Nuevo'' else emp_estado end) + '' - '' + exp_apellidos_nombres + '' ('' + plz_nombre + '')'' emp_nombre_plaza, exp_codigo_alternativo from exp.emp_empleos join exp.exp_expedientes on exp_codigo = emp_codexp join eor.plz_plazas on plz_codigo = emp_codplz where exists (select null from sco.permiso_empleo_tabla(''$$USER$$'') where emp_codigo = codemp) and (isnull(''@texto'', ''null'') = ''null'' or cast(emp_codigo as varchar) = ''@texto'' or exp_codigo_alternativo like ''%@texto%'' or exp_apellidos_nombres like ''%@texto%'' or plz_nombre like ''%@texto%'')&#xA;  &lt;/Bound&gt;&lt;/List&gt;" vli_usuario_grabacion="EVA" />
  <lista vli_id="EmpleadosASustituirDeConcursoYCompaniaEnviado" vli_description="Lista los empleados a sustituir de la compañía y concurso enviado" vli_value_source="UseItemData" vli_codfld="string" vli_enable_caching="0" vli_caching_expiration="0" vli_caching_expiration_mins="0" vli_definition="&lt;List&gt;&lt;Bound&gt;&#xA;    select emp_codigo, exp_apellidos_nombres + '' ('' + plz_nombre + '')''&#xA;    from rys.sec_sust_emp_concurso&#xA;      join exp.emp_empleos on emp_codigo = sec_codemp&#xA;      join exp.exp_expedientes on exp_codigo = emp_codexp&#xA;      join eor.plz_plazas on plz_codigo = emp_codplz&#xA;     where plz_codcia = SUBSTRING(''@codcos_codcia'', CHARINDEX(''|'', ''@codcos_codcia'', 0) + 1, LEN(''@codcos_codcia'') - CHARINDEX(''|'', ''@codcos_codcia'', 0)) and &#xA;           sec_codcos = SUBSTRING(''@codcos_codcia'', 1, CHARINDEX(''|'', ''@codcos_codcia'', 0) - 1)&#xA;  &lt;/Bound&gt;&lt;/List&gt;" vli_usuario_grabacion="EVA" />
  <lista vli_id="EmpleadosConEmpleosActivosYConUsuario" vli_description="Muestra todos los empleados con sus empleos que son activos" vli_prompt="" vli_value_source="UseItemData" vli_codfld="int" vli_enable_caching="0" vli_caching_expiration="0" vli_caching_expiration_mins="0" vli_definition="&lt;List&gt;&lt;Bound&gt;&#xA;    select emp_codigo, exp_apellidos_nombres + '' (''+ pue_nombre + '')'' from  exp.emp_empleos join exp.exp_expedientes on exp_codigo = emp_codexp join eor.plz_plazas on plz_codigo = emp_codplz join sec.eus_expediente_usuario on eus_codexp = exp_codigo join eor.pue_puestos on pue_codigo = plz_codpue where emp_estado = ''A''&#xA;  &lt;/Bound&gt;&lt;/List&gt;" vli_usuario_grabacion="EVA" />
  <lista vli_id="EmpleadosEvaluadoresDeCompaniaEnviada" vli_description="Lista los empleados evaluadores de la compañia enviada para el estudio socio-economico de los candidatos" vli_value_source="UseItemData" vli_codfld="string" vli_enable_caching="0" vli_caching_expiration="0" vli_caching_expiration_mins="0" vli_definition="&lt;List&gt;&lt;Bound&gt;&#xA;  select exp_codigo_alternativo, &#xA;         exp_apellidos_nombres &#xA;    from exp.exp_expedientes v1&#xA;   where exists(select 1 &#xA;                  from exp.eme_emp_evaluadores_v v2&#xA;                 where ccp_codigo &amp;gt;= 1&#xA;                   and ccp_codigo &amp;lt;= 3&#xA;                   and cia_codigo = @codcia&#xA;                   and v2.exp_codigo = v1.exp_codigo)&#xA;order by exp_apellidos_nombres&#xA;  &lt;/Bound&gt;&lt;/List&gt;" vli_usuario_grabacion="EVA" />
  <lista vli_id="EmpleadosFirmantesContrato" vli_description="Empleados que tienen marcado firma contrato" vli_value_source="UseSqlExpressionInItemData" vli_codfld="string" vli_enable_caching="0" vli_caching_expiration="0" vli_caching_expiration_mins="0" vli_definition="&lt;List&gt;&lt;Bound&gt;&#xA;   SELECT exp_codigo, exp_nombres_apellidos&#xA;FROM exp.emp_empleos&#xA;JOIN exp.exp_expedientes ON exp_expedientes.exp_codigo = emp_empleos.emp_codexp &#xA;WHERE isnull(gen.get_pb_field_data(emp_property_bag_data,''emp_firmante''), ''N'') = ''S''&#xA;AND emp_estado = ''A''&#xA;  &lt;/Bound&gt;&lt;/List&gt;" vli_usuario_grabacion="EVA" />
  <lista vli_id="EmpleadosRetiradosSinLiquidacion" vli_description="Lista los empleados en estado retirado que tienen un retiro y no poseen la liquidación" vli_prompt="Seleccionar" vli_value_source="UseItemData" vli_codfld="string" vli_enable_caching="0" vli_caching_expiration="0" vli_caching_expiration_mins="0" vli_definition="&lt;List&gt;&lt;Bound&gt;&#xA;    select exp_codigo_alternativo, exp_nombres_apellidos from exp.exp_expedientes join exp.emp_empleos on emp_codexp = exp_codigo join eor.plz_plazas on plz_codigo = emp_codplz join acc.ret_retiros on ret_codemp = emp_codigo where ((ret_estado = ''Autorizado'' and emp_estado = ''R'') or cfg.get_parametro_bit(''LiquidacionUtilizaRetirosNoAutorizados'') = 1) and not exists (select 1 from acc.lie_liquidaciones where lie_codret = ret_codigo and lie_codigo != @codlie and lie_estado != ''Denegado'') and plz_codcia = $$CODCIA$$ and exists (select null from sco.permiso_empleo_tabla(''$$USER$$'') where emp_codigo = codemp)&#xA;  &lt;/Bound&gt;&lt;/List&gt;" vli_usuario_grabacion="EVA" />
  <lista vli_id="EmpleosActDeEmpleoEnviado" vli_description="Lista los empleos activos del grupo corporativo al que pertenece el empleo enviado" vli_value_source="UseItemData" vli_codfld="int" vli_enable_caching="0" vli_caching_expiration="0" vli_caching_expiration_mins="0" vli_definition="&lt;List&gt;&lt;Bound&gt;&#xA;select exp_codigo_alternativo,&#xA;       exp_apellidos_nombres &#xA;  from exp.exp_expedientes&#xA; where exists(&#xA;        select null &#xA;          from exp.emp_empleos&#xA;          join eor.plz_plazas on plz_codigo = emp_codplz&#xA;          join eor.cia_companias on cia_codigo = plz_codcia&#xA;         where emp_codexp = exp_codigo&#xA;           and cia_codgrc in (&#xA;                select cia_codgrc &#xA;                  from exp.emp_empleos e1 &#xA;                  join eor.plz_plazas p1 on p1.plz_codigo = e1.emp_codplz &#xA;                  join eor.cia_companias c1 on c1.cia_codigo = p1.plz_codcia &#xA;                 where e1.emp_codigo = @codemp)&#xA;           and emp_estado = ''A''&#xA;           and exists (select null from sco.permiso_empleo_tabla(''$$USER$$'') where emp_codigo = codemp))&#xA;&lt;/Bound&gt;&lt;/List&gt;" vli_usuario_grabacion="EVA" />
  <lista vli_id="EmpleosActivosAutDeCompaniaSeleccionada" vli_description="Lista los empleos activos autorizados de la compañía seleccionada, con el nombre del empleado y su plaza como texto" vli_value_source="UseItemData" vli_codfld="int" vli_enable_caching="0" vli_caching_expiration="0" vli_caching_expiration_mins="0" vli_definition="&lt;List&gt;&lt;Bound&gt;&#xA;    select emp_codigo, exp_apellidos_nombres  + '' ('' + plz_nombre + '')'' emp_nombre_plaza&#xA;  from exp.emp_empleos &#xA;  join exp.exp_expedientes on exp_codigo = emp_codexp &#xA;  join eor.plz_plazas on plz_codigo = emp_codplz&#xA; where emp_estado = ''A'' and plz_codcia = $$CODCIA$$ and exists (select null from sco.permiso_empleo_tabla(''$$USER$$'') where emp_codigo = codemp)&#xA; order by exp_apellidos_nombres&#xA;  &lt;/Bound&gt;&lt;/List&gt;" vli_usuario_grabacion="EVA" />
  <lista vli_id="EmpleosActivosAutPoratlDeExpedienteEnviado" vli_description="Lista los empleos activos autorizados según la preferencia del portal del expediente enviado" vli_value_source="UseItemData" vli_codfld="int" vli_enable_caching="0" vli_caching_expiration="0" vli_caching_expiration_mins="0" vli_definition="&lt;List&gt;&lt;Bound&gt;&#xA;    select emp_codigo, plz_nombre&#xA;  from exp.emp_empleos&#xA;  join eor.plz_plazas on plz_codigo = emp_codplz&#xA;  join exp.exp_expedientes on exp_codigo = emp_codexp&#xA; where emp_estado = ''A''&#xA;   and exp_codigo_alternativo = ''@codigo_alternativo''&#xA;   and exists(select null from sco.permiso_emp_x_jerarquia_tabla(''$$USER$$'') where emp_codigo = codemp)&#xA;   order by plz_nombre&#xA;  &lt;/Bound&gt;&lt;/List&gt;" vli_usuario_grabacion="EVA" />
  <lista vli_id="EmpleosActivosCodAlternativoFiltradosPorCiaCdtUni" vli_description="Lista los empleos activos del código alternativo enviado filtrados por el código de compañía, centros de trabajo y unidad" vli_value_source="UseItemData" vli_codfld="int" vli_enable_caching="0" vli_caching_expiration="0" vli_caching_expiration_mins="0" vli_definition="&lt;List&gt;&lt;Bound&gt;&#xA;    select emp_codigo, plz_nombre&#xA;      from exp.emp_empleos&#xA;      join exp.exp_expedientes on exp_codigo = emp_codexp&#xA;      join eor.plz_plazas on plz_codigo = emp_codplz&#xA;      join eor.cia_companias on cia_codigo = plz_codcia&#xA;      join eor.cdt_centros_de_trabajo on cdt_codigo = plz_codcdt&#xA;      join eor.uni_unidades on uni_codigo = plz_coduni&#xA;     where plz_codcia = @codcia &#xA;       and (isnull(@codcdt, ''-1'') = ''-1'' or plz_codcdt = @codcdt) &#xA;       and (isnull(@coduni, ''-1'') = ''-1'' or plz_coduni = @coduni)&#xA;       and emp_estado = ''A''&#xA;     and exp_codigo_alternativo = ''@codigo_alternativo''&#xA;  &lt;/Bound&gt;&lt;/List&gt;" vli_usuario_grabacion="EVA" />
  <lista vli_id="EmpleosActivosDeCodigoAlternativoEnviado" vli_description="Lista los empleos activos para un expediente de codigo alternativo enviado" vli_value_source="UseItemData" vli_codfld="int" vli_enable_caching="0" vli_caching_expiration="0" vli_caching_expiration_mins="0" vli_definition="&lt;List&gt;&lt;Bound&gt;&#xA;    select emp_codigo, plz_nombre&#xA;  from exp.emp_empleos&#xA;  join eor.plz_plazas on plz_codigo = emp_codplz&#xA;  join exp.exp_expedientes on exp_codigo = emp_codexp&#xA; where emp_estado = ''A''&#xA;   and exp_codigo_alternativo = ''@codigo_alternativo''&#xA;  &lt;/Bound&gt;&lt;/List&gt;" vli_usuario_grabacion="EVA" />
  <lista vli_id="EmpleosActivosDeCodigoAlternativoEnviadoPorGrupo" vli_description="Lista los empleos activos que pertenecen al grupo seleccionado de un codigo alternativo enviado" vli_prompt="Empleado" vli_value_source="UseItemData" vli_codfld="int" vli_enable_caching="0" vli_caching_expiration="0" vli_caching_expiration_mins="0" vli_definition="&lt;List&gt;&lt;Bound&gt;&#xA;    select emp_codigo, plz_nombre&#xA;    from exp.emp_empleos &#xA;    join eor.plz_plazas on plz_codigo = emp_codplz&#xA;    join eor.cia_companias on cia_codigo = plz_codcia&#xA;    join exp.exp_expedientes on exp_codigo = emp_codexp&#xA;     where emp_estado = ''A''&#xA;     and exp_codigo_alternativo = ''@codigo_alternativo''&#xA;     and exists (select null from sco.permiso_empleo_tabla(''$$USER$$'') where emp_codigo = codemp)&#xA;     and cia_codgrc = $$CODGRC$$&#xA;  &lt;/Bound&gt;&lt;/List&gt;" vli_usuario_grabacion="EVA" />
  <lista vli_id="EmpleosActivosDeExpedienteAlternativoEnviado" vli_description="Lista los empleados activos de un expediente enviado y verificando seguridad por compañía" vli_value_source="UseItemData" vli_codfld="int" vli_enable_caching="0" vli_caching_expiration="0" vli_caching_expiration_mins="0" vli_definition="&lt;List&gt;&lt;Bound&gt;&#xA;select emp_codigo, plz_nombre &#xA;  from exp.emp_empleos &#xA;  join eor.plz_plazas on plz_codigo = emp_codplz &#xA;  join exp.exp_expedientes on exp_codigo = emp_codexp &#xA; where emp_estado = ''A'' and exp_codigo_alternativo = ''@codigo_alternativo'' and plz_codcia = $$CODCIA$$ and exists (select null from sco.permiso_empleo_tabla(''$$USER$$'') where emp_codigo = codemp)&#xA;&lt;/Bound&gt;&lt;/List&gt;" vli_usuario_grabacion="EVA" />
  <lista vli_id="EmpleosActivosDeExpedienteEnviado" vli_description="Lista los empleos activos de un expediente enviado" vli_prompt="Empleado" vli_value_source="UseItemData" vli_codfld="int" vli_enable_caching="1" vli_caching_expiration="2" vli_caching_expiration_mins="1" vli_definition="&lt;List&gt;&lt;Bound&gt;&#xA;    select emp_codigo, plz_nombre from exp.emp_empleos&#xA;join eor.plz_plazas on plz_codigo = emp_codplz&#xA;where emp_codexp = @codexp and emp_estado = ''A''&#xA;  &lt;/Bound&gt;&lt;/List&gt;" vli_usuario_grabacion="EVA" />
  <lista vli_id="EmpleosActivosDeExpedienteEvaluadorEnviado" vli_description="Lista los empleos activos del expediente evaluador que estan activos" vli_value_source="UseItemData" vli_codfld="int" vli_enable_caching="0" vli_caching_expiration="0" vli_caching_expiration_mins="0" vli_definition="&lt;List&gt;&lt;Bound&gt;&#xA;  select emp_codigo, plz_nombre from exp.emp_empleos&#xA;    join eor.plz_plazas on plz_codigo = emp_codplz&#xA;    where emp_codexp = @codexp and emp_estado = ''A''&#xA;  &lt;/Bound&gt;&lt;/List&gt;" vli_usuario_grabacion="EVA" />
  <lista vli_id="EmpleosActivosDeExpedienteYCompaniaEnviado" vli_description="Lista los empleados activos de un expediente enviado y que pertenecen a una compañía enviada" vli_value_source="UseItemData" vli_codfld="int" vli_enable_caching="0" vli_caching_expiration="0" vli_caching_expiration_mins="0" vli_definition="&lt;List&gt;&lt;Bound&gt;&#xA;    select emp_codigo, plz_nombre &#xA;    from exp.emp_empleos&#xA;    join eor.plz_plazas on plz_codigo = emp_codplz&#xA;    join exp.exp_expedientes on exp_codigo = emp_codexp&#xA;   where emp_estado = ''A'' and exp_codigo_alternativo = ''@codigo_alternativo'' and plz_codcia = @codcia and exists (select null from sco.permiso_empleo_tabla(''$$USER$$'') where emp_codigo = codemp)&#xA;  &lt;/Bound&gt;&lt;/List&gt;" vli_usuario_grabacion="EVA" />
  <lista vli_id="EmpleosActivosDeExpedienteYGrupoEnviado" vli_description="Lista los empleos activos que pertenecen al expediente enviado y que son del grupo enviado" vli_prompt="Empleado" vli_value_source="UseItemData" vli_codfld="int" vli_enable_caching="0" vli_caching_expiration="0" vli_caching_expiration_mins="0" vli_definition="&lt;List&gt;&lt;Bound&gt;&#xA;    select emp_codigo, plz_nombre&#xA;    from exp.emp_empleos &#xA;    join eor.plz_plazas on plz_codigo = emp_codplz&#xA;    join eor.cia_companias on cia_codigo = plz_codcia&#xA;    join exp.exp_expedientes on exp_codigo = emp_codexp&#xA;     where emp_estado = ''A''&#xA;     and exp_codigo = @codexp&#xA;     and cia_codgrc = @codgrc&#xA;  &lt;/Bound&gt;&lt;/List&gt;" vli_usuario_grabacion="EVA" />
  <lista vli_id="EmpleosActivosEvaluadoresDeCodAlternativoEnviado" vli_description="Lista los empleos evaluadores activos de un codigo alternativo de expediente enviado" vli_value_source="UseItemData" vli_codfld="int" vli_enable_caching="0" vli_caching_expiration="0" vli_caching_expiration_mins="0" vli_definition="&lt;List&gt;&lt;Bound&gt;&#xA;    select emp_codigo, plz_nombre &#xA;      from exp.eme_emp_evaluadores_v&#xA;     where ccp_codigo &amp;gt;= 1&#xA;       and ccp_codigo &amp;lt;= 3&#xA;       and cia_codigo = @codcia&#xA;       and exp_codigo_alternativo = ''@codigo_alternativo''&#xA;  &lt;/Bound&gt;&lt;/List&gt;" vli_usuario_grabacion="EVA" />
  <lista vli_id="EmpleosActivosExpedientePlazaPorCompania" vli_description="Lista de empleados que muestra el nombre del empleado concatenado con la plaza" vli_value_source="UseItemData" vli_codfld="int" vli_enable_caching="0" vli_caching_expiration="0" vli_caching_expiration_mins="0" vli_definition="&lt;List&gt;&lt;Bound&gt;&#xA;select emp_codigo, exp_apellidos_nombres +'' - ''+ plz_nombre + '' ('' + case emp_estado when ''A'' then ''{Activo}'' else ''{Retirado}'' end + '')'' plz_nombre from exp.emp_empleos join eor.plz_plazas on emp_codplz = plz_codigo join exp.exp_expedientes on exp_codigo = emp_codexp where emp_estado in (''A'', ''R'') and plz_codcia = $$CODCIA$$ and exists (select null from sco.permiso_empleo_tabla(''$$USER$$'') where emp_codigo = codemp) order by emp_estado&#xA;&lt;/Bound&gt;&lt;/List&gt;" vli_usuario_grabacion="EVA" />
  <lista vli_id="EmpleosActivosExpedientePorCompania" vli_description="Lista los empleos activos para un expediente con plazas de la compañia" vli_prompt="Empleado" vli_value_source="UseItemData" vli_codfld="int" vli_enable_caching="0" vli_caching_expiration="0" vli_caching_expiration_mins="1" vli_definition="&lt;List&gt;&lt;Bound&gt;&#xA;    select emp_codigo, convert(varchar, plz_codigo) + '' - '' + plz_nombre + '' ('' + case emp_estado when ''A'' then ''{Activo}'' else ''{Retirado}'' end + '')'' plz_nombre from exp.emp_empleos join eor.plz_plazas on emp_codplz = plz_codigo where emp_codexp = @codexp and emp_estado in (''A'', ''R'') and plz_codcia = $$CODCIA$$ and exists (select null from sco.permiso_empleo_tabla(''$$USER$$'') where emp_codigo = codemp) order by emp_estado&#xA;  &lt;/Bound&gt;&lt;/List&gt;" vli_usuario_grabacion="EVA" />
  <lista vli_id="EmpleosActivosJefesDeCodigoAlternativoEnviado" vli_description="Lista los empleos activos que tienen algun subalterno de un expediente por el código alternativo enviado" vli_value_source="UseItemData" vli_codfld="int" vli_enable_caching="0" vli_caching_expiration="0" vli_caching_expiration_mins="0" vli_definition="&lt;List&gt;&lt;Bound&gt;&#xA;  select distinct emp_codigo, plz_nombre&#xA;      from exp.emp_empleos&#xA;      join eor.pjf_plaza_jefes on pjf_codplz_jefe = emp_codplz&#xA;      join eor.plz_plazas on plz_codigo = emp_codplz&#xA;      join exp.exp_expedientes on exp_codigo = emp_codexp&#xA;     where emp_codexp = exp_codigo &#xA;       and emp_estado = ''A''&#xA;     and exp_codigo_alternativo = ''@codigo_alternativo''&#xA;  &lt;/Bound&gt;&lt;/List&gt;" vli_usuario_grabacion="EVA" />
  <lista vli_id="EmpleosActivosPermitidosPortal" vli_description="Lista los empleos activos para un expediente con seguridad de contenido" vli_value_source="UseItemData" vli_codfld="int" vli_enable_caching="0" vli_caching_expiration="0" vli_caching_expiration_mins="0" vli_definition="&lt;List&gt;&lt;Bound&gt;&#xA;    select emp_codigo, plz_nombre from exp.emp_empleos join eor.plz_plazas on emp_codplz = plz_codigo where emp_codexp = @codexp and emp_estado in (''A'', ''R'') and exists (select null from sco.permiso_empleo_tabla(''$$USER$$'') where emp_codigo = codemp)&#xA;  &lt;/Bound&gt;&lt;/List&gt;" vli_usuario_grabacion="EVA" />
  <lista vli_id="EmpleosAEvaluarDeEmpleoEvaluadorDeExpEnviado" vli_description="Lista los empleos a evaluar de código alternativo enviado para un período y empleo evaluador enviado" vli_value_source="UseItemData" vli_codfld="int" vli_enable_caching="0" vli_caching_expiration="0" vli_caching_expiration_mins="0" vli_definition="&lt;List&gt;&lt;Bound&gt;&#xA;  select emp_codigo, plz_nombre&#xA;    from exp.emp_empleos&#xA;    join exp.exp_expedientes on exp_codigo = emp_codexp&#xA;    join eor.plz_plazas on plz_codigo = emp_codplz&#xA;   where exists(select null from eva.ase_asigna_evaluadores &#xA;                  join exp.emp_empleos e1 on e1.emp_codplz = ase_codplz_evaluador&#xA;                 where ase_codplz = plz_codigo&#xA;                   and emp_codigo = @codemp_evaluador&#xA;                   and ase_codpev = @codpev)&#xA;    and exp_codigo_alternativo = ''@codigo_alternativo''&#xA;  and emp_estado = ''A''&#xA;order by plz_nombre&#xA;  &lt;/Bound&gt;&lt;/List&gt;" vli_usuario_grabacion="EVA" />
