  <lista vli_id="EmpleosAutReclutamientoCodigoAlternativoEnviado" vli_description="Listas los empleos de reclutamiento de un codigo alternativo enviado" vli_value_source="UseItemData" vli_codfld="int" vli_enable_caching="0" vli_caching_expiration="0" vli_caching_expiration_mins="0" vli_definition="&lt;List&gt;&lt;Bound&gt;&#xA;  select emp_codigo, plz_nombre &#xA;      from exp.emr_emp_reclutamiento_v&#xA;     where emp_estado = ''A''&#xA;       and exists (select null from sco.permiso_empleo_tabla(''$$USER$$'') where emp_codigo = codemp)&#xA;       and exp_codigo_alternativo = ''@codigo_alternativo''&#xA;     and cia_codgrc = $$CODGRC$$&#xA;  &lt;/Bound&gt;&lt;/List&gt;" vli_usuario_grabacion="EVA" />
  <lista vli_id="EmpleosConContratacionesAutDePortal" vli_description="Listado de empleos que se generaron a partir de la acción de contratación a los que el usuario autenticado tiene permiso" vli_value_source="UseItemData" vli_codfld="string" vli_enable_caching="0" vli_caching_expiration="0" vli_caching_expiration_mins="0" vli_definition="&lt;List&gt;&lt;Bound&gt;&#xA;select con_codemp_generado, plz_nombre&#xA;from exp.emp_empleos &#xA;join exp.exp_expedientes on exp_codigo = emp_codexp&#xA;join eor.plz_plazas on plz_codigo = emp_codplz&#xA;join acc.con_contrataciones on con_codemp_generado = emp_codigo&#xA;where con_estado = ''Autorizado'' and con_codemp_generado is not null&#xA;  and not exists (select 1 from acc.rco_reversiones_contratos where rco_codcon = con_codigo and rco_estado != ''Denegado'')&#xA;  and exists(select null from sco.permiso_emp_x_jerarquia_tabla(''$$USER$$'') where con_codemp_generado = codemp) and exp_codigo_alternativo = ''@codigo_alternativo''&#xA;  and convert(date, con_contrato_ini) &amp;gt;= (convert(date, (getdate() - cfg.get_parametro_int(''NumeroDiasAnterioresALaActualParaRevertirContrataciones''))))&#xA;&lt;/Bound&gt;&lt;/List&gt;" vli_usuario_grabacion="EVA" />
  <lista vli_id="EmpleosConContratacionesDeCiaSeleccionada" vli_description="Listado de empleos que se generaron a partir de la acción de contratación" vli_value_source="UseItemData" vli_codfld="string" vli_enable_caching="0" vli_caching_expiration="0" vli_caching_expiration_mins="0" vli_definition="&lt;List&gt;&lt;Bound&gt;&#xA;select emp_codigo, plz_nombre&#xA;from exp.emp_empleos &#xA;join exp.exp_expedientes on exp_codigo = emp_codexp&#xA;join eor.plz_plazas on plz_codigo = emp_codplz&#xA;join acc.con_contrataciones on con_codemp_generado = emp_codigo&#xA;where con_estado = ''Autorizado'' and con_codemp_generado is not null&#xA;  and not exists (select 1 from acc.rco_reversiones_contratos where rco_codcon = con_codigo and rco_estado != ''Denegado'')&#xA;  and plz_codcia = $$CODCIA$$&#xA;  and exists (select null from sco.permiso_plaza_tabla(''$$USER$$'') where plz_codigo = codplz) and exp_codigo_alternativo = ''@codigo_alternativo''&#xA;  and convert(date, con_contrato_ini) &amp;gt;= (convert(date, (getdate() - cfg.get_parametro_int(''NumeroDiasAnterioresALaActualParaRevertirContrataciones''))))&#xA;&lt;/Bound&gt;&lt;/List&gt;" vli_usuario_grabacion="EVA" />
  <lista vli_id="EmpleosEvaluadoresActivosCodAlternativoEnviado" vli_description="Lista los empleos activos del codigo alternativo enviado y que tiene asociado una plaza evaluadora" vli_value_source="UseItemData" vli_codfld="int" vli_enable_caching="0" vli_caching_expiration="0" vli_caching_expiration_mins="0" vli_definition="&lt;List&gt;&lt;Bound&gt;&#xA;  select emp_codigo, plz_nombre&#xA;      from exp.emp_empleos&#xA;      join exp.exp_expedientes on exp_codigo = emp_codexp&#xA;      join eor.plz_plazas on plz_codigo = emp_codplz&#xA;      join eor.cia_companias on cia_codigo = plz_codcia&#xA;     where exp_codigo_alternativo = ''@codigo_alternativo''&#xA;       and emp_estado = ''A''&#xA;       and cia_codgrc = @codgrc&#xA;       and exists (select null from sco.permiso_empleo_tabla(''$$USER$$'') where emp_codigo = codemp)&#xA;       and exists(select null from eva.ase_asigna_evaluadores where ase_codplz_evaluador = emp_codplz and ase_codpev = @codpev)&#xA;  &lt;/Bound&gt;&lt;/List&gt;" vli_usuario_grabacion="EVA" />
  <lista vli_id="EmpleosEvaluadoresDeEmpleoEvaluadoYExpEnviado" vli_description="Lista los empleos activos del codigo alternativo enviado y cuya plaza tiene asignaciones como evaluador para un período y empleo a evaluar enviado" vli_value_source="UseItemData" vli_codfld="int" vli_enable_caching="0" vli_caching_expiration="0" vli_caching_expiration_mins="0" vli_definition="&lt;List&gt;&lt;Bound&gt;&#xA;  select distinct emp_codigo, plz_nombre&#xA;  from eva.ase_asigna_evaluadores&#xA;  join eor.plz_plazas on plz_codigo = ase_codplz_evaluador&#xA;  join exp.emp_empleos on emp_codplz = plz_codigo&#xA;  join exp.exp_expedientes on exp_codigo = emp_codexp&#xA;   and ase_codpev = @codpev&#xA;   and emp_estado = ''A''&#xA;   and exp_codigo_alternativo = ''@codigo_alternativo''&#xA;   and ase_codplz = (select emp_codplz from exp.emp_empleos e where e.emp_codigo = @codemp_evaluado)&#xA;  &lt;/Bound&gt;&lt;/List&gt;" vli_usuario_grabacion="EVA" />
  <lista vli_id="EmpleosEvaluadoresDeUsuarioDePeriodoEnviado" vli_description="Lista los empleos evaluadores para un período enviado del usuario autenticado" vli_value_source="UseItemData" vli_codfld="int" vli_enable_caching="0" vli_caching_expiration="0" vli_caching_expiration_mins="0" vli_definition="&lt;List&gt;&lt;Bound&gt;&#xA;  select emp_codigo, plz_nombre&#xA;    from exp.emp_empleos&#xA;    join exp.exp_expedientes on exp_codigo = emp_codexp&#xA;    join eor.plz_plazas on plz_codigo = emp_codplz&#xA;   where exp_codigo = (select eus_codexp from sec.eus_expediente_usuario join sec.usr_users on usr_codigo = eus_codusr where usr_username = ''$$USER$$'')&#xA;     and exists(select null from eva.ase_asigna_evaluadores where ase_codplz_evaluador = emp_codplz and ase_codpev = @codpev)&#xA;   and emp_estado = ''A''&#xA;  order by plz_nombre&#xA;  &lt;/Bound&gt;&lt;/List&gt;" vli_usuario_grabacion="EVA" />
  <lista vli_id="EmpleosEvaluadosConAsignaDePeriodoYExp" vli_description="Lista los empleos activos del codigo alternativo enviado y cuya plaza tiene asignaciones como evaluado para un período enviado" vli_value_source="UseItemData" vli_codfld="int" vli_enable_caching="0" vli_caching_expiration="0" vli_caching_expiration_mins="0" vli_definition="&lt;List&gt;&lt;Bound&gt;&#xA;  select distinct emp_codigo, plz_nombre&#xA;      from eva.ase_asigna_evaluadores&#xA;      join eor.plz_plazas on plz_codigo = ase_codplz&#xA;      join exp.emp_empleos on emp_codplz = plz_codigo&#xA;      join exp.exp_expedientes on exp_codigo = emp_codexp&#xA;       and ase_codpev = @codpev&#xA;       and emp_estado = ''A''&#xA;       and exp_codigo_alternativo = ''@codigo_alternativo''&#xA;  &lt;/Bound&gt;&lt;/List&gt;" vli_usuario_grabacion="EVA" />
  <lista vli_id="EmpleosEvaluadosDeUsuarioDePeriodoEnviado" vli_description="Lista los empleos que seran evaluados para un período enviado del usuario autenticado" vli_value_source="UseItemData" vli_codfld="int" vli_enable_caching="0" vli_caching_expiration="0" vli_caching_expiration_mins="0" vli_definition="&lt;List&gt;&lt;Bound&gt;&#xA;  select emp_codigo, plz_nombre&#xA;    from exp.emp_empleos&#xA;    join exp.exp_expedientes on exp_codigo = emp_codexp&#xA;    join eor.plz_plazas on plz_codigo = emp_codplz&#xA;   where exp_codigo = (select eus_codexp from sec.eus_expediente_usuario join sec.usr_users on usr_codigo = eus_codusr where usr_username = ''$$USER$$'')&#xA;     and exists(select null from eva.ase_asigna_evaluadores where ase_codplz = emp_codplz and ase_codpev = @codpev)&#xA;   and emp_estado = ''A''&#xA;  order by plz_nombre&#xA;&lt;/Bound&gt;&lt;/List&gt;" vli_usuario_grabacion="EVA" />
