  <lista vli_id="EmpleosRetiradosAutorizadosDeCiaSeleccionada" vli_description="Listado de empleos retirados que están autorizados para la compañía y codigo alternativo enviado enviada" vli_value_source="UseItemData" vli_codfld="string" vli_enable_caching="0" vli_caching_expiration="0" vli_caching_expiration_mins="0" vli_definition="&lt;List&gt;&lt;Bound&gt;&#xA;select&#xA;    emp_codigo, ''[''+CONVERT(VARCHAR, emp_fecha_ingreso, 103) + '' - ''+CONVERT(VARCHAR, emp_fecha_retiro, 103)+''] '' + plz_nombre as plz_nombre&#xA;from exp.emp_empleos &#xA;join exp.exp_expedientes on exp_codigo = emp_codexp&#xA;join eor.plz_plazas on plz_codigo = emp_codplz&#xA;join acc.ret_retiros on ret_codemp = emp_codigo&#xA;where ret_estado = ''Autorizado'' and emp_estado = ''R''&#xA;  and not exists (select 1 from acc.rre_reversiones_retiro where rre_codret = ret_codigo and rre_estado != ''Denegado'')&#xA;  and plz_codcia = $$CODCIA$$&#xA;  and exists (select null from sco.permiso_empleo_tabla(''$$USER$$'') where emp_codigo = codemp) and exp_codigo_alternativo = ''@codigo_alternativo''&#xA;  and convert(date, ret_fecha_retiro) &amp;gt;= (convert(date, (getdate() - cfg.get_parametro_int(''NumeroDiasAnterioresALaActualParaRevertirRetiros''))))&#xA;&lt;/Bound&gt;&lt;/List&gt;" vli_usuario_grabacion="EVA" />
  <lista vli_id="EmpleosRetiradosAutPoratlDeExpedienteEnviado" vli_description="Listado de empleos retirados que están autorizados para la compañía y codigo alternativo enviado enviada" vli_value_source="UseItemData" vli_codfld="string" vli_enable_caching="0" vli_caching_expiration="0" vli_caching_expiration_mins="0" vli_definition="&lt;List&gt;&lt;Bound&gt;&#xA;select&#xA;    emp_codigo, ''[''+CONVERT(VARCHAR, emp_fecha_ingreso, 103) + '' - ''+CONVERT(VARCHAR, emp_fecha_retiro, 103)+''] '' + plz_nombre as plz_nombre&#xA;from exp.emp_empleos &#xA;join exp.exp_expedientes on exp_codigo = emp_codexp&#xA;join eor.plz_plazas on plz_codigo = emp_codplz&#xA;join acc.ret_retiros on ret_codemp = emp_codigo&#xA;where ret_estado = ''Autorizado'' and emp_estado = ''R''&#xA;  and not exists (select 1 from acc.rre_reversiones_retiro where rre_codret = ret_codigo and rre_estado != ''Denegado'')&#xA;  and exists(select null from sco.permiso_emp_x_jerarquia_tabla(''$$USER$$'') where emp_codigo = codemp) and exp_codigo_alternativo = ''@codigo_alternativo''&#xA;  and convert(date, ret_fecha_retiro) &amp;gt;= (convert(date, (getdate() - cfg.get_parametro_int(''NumeroDiasAnterioresALaActualParaRevertirRetiros''))))&#xA;&lt;/Bound&gt;&lt;/List&gt;" vli_usuario_grabacion="EVA" />
  <lista vli_id="EmpleosRetiradosSinLiquidacion" vli_description="Lista los empleos en estado retirado que tienen un retiro y no poseen la liquidación" vli_prompt="Seleccionar" vli_value_source="UseItemData" vli_codfld="string" vli_enable_caching="0" vli_caching_expiration="0" vli_caching_expiration_mins="0" vli_definition="&lt;List&gt;&lt;Bound&gt;&#xA;    select emp_codigo, plz_nombre from exp.emp_empleos  join exp.exp_expedientes on exp_codigo = emp_codexp   join eor.plz_plazas on plz_codigo = emp_codplz  join acc.ret_retiros on ret_codemp = emp_codigo   where ((ret_estado = ''Autorizado'' and emp_estado = ''R'' )  or cfg.get_parametro_bit(''LiquidacionUtilizaRetirosNoAutorizados'') = 1) and not exists (select 1 from acc.lie_liquidaciones where lie_codret = ret_codigo and lie_codigo != @codlie and lie_estado != ''Denegado'')   and plz_codcia = $$CODCIA$$ and exists (select null from sco.permiso_empleo_tabla(''$$USER$$'') where emp_codigo = codemp) and exp_codigo_alternativo = ''@codigo_alternativo''&#xA;  &lt;/Bound&gt;&lt;/List&gt;" vli_usuario_grabacion="EVA" />
