  <lista vli_id="PeriodosEnEvaluacionDeExpedienteEnviado" vli_description="Lista los períodos en estado de evaluación de los grupos de los empleos del expediente enviado" vli_value_source="UseItemData" vli_codfld="int" vli_enable_caching="0" vli_caching_expiration="0" vli_caching_expiration_mins="0" vli_definition="&lt;List&gt;&lt;Bound&gt;&#xA;  select pev_codigo, pev_descripcion &#xA;      from eva.pev_periodos&#xA;     where pev_estado = ''Evaluacion''&#xA;     and pev_codgrc in (&#xA;          select cia_codgrc from exp.emp_empleos&#xA;                join eor.plz_plazas on plz_codigo = emp_codplz&#xA;                join eor.cia_companias on cia_codigo = plz_codcia&#xA;               where emp_codexp = @codexp)&#xA;  &lt;/Bound&gt;&lt;/List&gt;" vli_usuario_grabacion="EVA" />
  <lista vli_id="PeriodosEnEvaluacionDeGrupoEnviado" vli_description="Lista los períodos de evaluacion que se encuentra en estado de Evaluacion" vli_value_source="UseItemData" vli_codfld="int" vli_enable_caching="0" vli_caching_expiration="0" vli_caching_expiration_mins="0" vli_definition="&lt;List&gt;&lt;Bound&gt;&#xA;  select pev_codigo, pev_descripcion from eva.pev_periodos&#xA;     where pev_codgrc = @codgrc and (pev_estado = ''Evaluacion'' or pev_estado = ''Planificacion'')&#xA;  &lt;/Bound&gt;&lt;/List&gt;" vli_usuario_grabacion="EVA" />
  <lista vli_id="PeriodosEnEvaluacionDeGrupoSeleccionado" vli_description="Lista los períodos de evaluacion que no están cerrados del grupo corporativo seleccionado" vli_value_source="UseItemData" vli_codfld="int" vli_enable_caching="0" vli_caching_expiration="0" vli_caching_expiration_mins="0" vli_definition="&lt;List&gt;&lt;Bound&gt;&#xA;  select pev_codigo, pev_descripcion &#xA;    from eva.pev_periodos&#xA;     where pev_codgrc = $$CODGRC$$ and pev_estado != ''Cerrado''&#xA;     order by pev_descripcion&#xA;&lt;/Bound&gt;&lt;/List&gt;" vli_usuario_grabacion="EVA" />
  <lista vli_id="PeriodosEvaluacionDesempeño" vli_description="Periodos de Evaluación de Desempeño" vli_prompt="Periodos" vli_value_source="UseItemData" vli_codfld="int" vli_enable_caching="0" vli_caching_expiration="0" vli_caching_expiration_mins="0" vli_definition="&lt;List&gt;&lt;Bound&gt;&#xA;    Select pev_codigo, pev_descripcion&#xA;    from eva.pev_periodos&#xA;  &lt;/Bound&gt;&lt;/List&gt;" vli_usuario_grabacion="EVA" />
  <lista vli_id="PeriodosEvaluacionEditablesDeGrupoEnviado" vli_description="Lista los períodos de evaluación que son editables del grupo corporativo enviado" vli_value_source="UseItemData" vli_codfld="int" vli_enable_caching="0" vli_caching_expiration="0" vli_caching_expiration_mins="0" vli_definition="&lt;List&gt;&lt;Bound&gt;&#xA;  select pev_codigo, pev_descripcion from eva.pev_periodos&#xA;     where pev_codgrc = @codgrc and pev_estado != ''Cerrado''&#xA;  &lt;/Bound&gt;&lt;/List&gt;" vli_usuario_grabacion="EVA" />
  <lista vli_id="PeriodosEvaluacionGrupoCorporativo" vli_description="Períodos Evaluación del Grupo Corporativo" vli_value_source="UseItemData" vli_codfld="int" vli_enable_caching="0" vli_caching_expiration="0" vli_caching_expiration_mins="0" vli_definition="&lt;List&gt;&lt;Bound&gt;&#xA;    select pev_codigo,pev_descripcion from eva.pev_periodos where pev_codgrc= $$CODGRC$$&#xA;&lt;/Bound&gt;&lt;/List&gt;" vli_usuario_grabacion="EVA" />
  <lista vli_id="PeriodosPlanillaDeEmpleadoYTipoPlanilla" vli_description="Periodos de tipo planilla enviada en las que le han pagado al empleado enviado" vli_prompt="Periodo" vli_value_source="UseItemData" vli_codfld="int" vli_enable_caching="1" vli_caching_expiration="2" vli_caching_expiration_mins="5" vli_definition="&lt;List&gt;&lt;Bound&gt;&#xA;select ppl_codigo, REPLACE(REPLACE(cfg.GetLocalized(''DescripcionPeriodoPlanilla'', ''PeriodoPlanilla'', ''es''), ''{{0}}'', convert(varchar, ppl_fecha_ini, 103)), ''{{1}}'', convert(varchar, ppl_fecha_fin, 103))&#xA;  from sal.ppl_periodos_planilla ppl&#xA; where exists(select null &#xA;                from sal.hpa_hist_periodos_planilla&#xA;               where hpa_codppl = ppl_codigo and cast(hpa_codemp as varchar) + ''|'' + CAST(ppl_codtpl as varchar) = ''@codemptpl'')&#xA;order by ppl_fecha_ini&#xA;  &lt;/Bound&gt;&lt;/List&gt;" vli_usuario_grabacion="EVA" />
  <lista vli_id="PeriodosPlanillaEnGeneracionDeCodigoTPLEnviado" vli_description="Lista los períodos en generación por el código de tipo de planilla enviado" vli_prompt="Seleccionar" vli_value_source="UseItemData" vli_codfld="string" vli_enable_caching="0" vli_caching_expiration="0" vli_caching_expiration_mins="0" vli_definition="&lt;List&gt;&lt;Bound&gt;&#xA;    select ppl_codigo_planilla, convert(varchar, ppl_fecha_ini, 103) + '' - '' + convert(varchar, ppl_fecha_fin, 103) periodo from sal.ppl_periodos_planilla where ppl_codtpl = ''@codtpl'' and ppl_estado = ''Generado''&#xA;  &lt;/Bound&gt;&lt;/List&gt;" vli_usuario_grabacion="EVA" />
  <lista vli_id="PeriodosPlanillaEnGeneracionDeTipoPlanillaEnviado" vli_description="Lista los períodos en generación por el tipo de planilla enviado" vli_prompt="Seleccionar" vli_value_source="UseItemData" vli_codfld="string" vli_enable_caching="0" vli_caching_expiration="0" vli_caching_expiration_mins="0" vli_definition="&lt;List&gt;&lt;Bound&gt;&#xA;    select ppl_codigo_planilla, convert(varchar, ppl_fecha_ini, 103) + '' - '' + convert(varchar, ppl_fecha_fin, 103) periodo from sal.ppl_periodos_planilla inner join sal.tpl_tipo_planilla on tpl_codigo = ppl_codtpl where tpl_codigo_visual = ''@codtpl_visual'' and tpl_codcia = $$CODCIA$$ and ppl_estado = ''Generado''&#xA;  &lt;/Bound&gt;&lt;/List&gt;" vli_usuario_grabacion="EVA" />
  <lista vli_id="PeriodosPlanillaEnRevisionGeneracionDeTipoPlanilla" vli_description="Lista los periodos de planilla por el codigo de tipo de planilla enviado" vli_prompt="PeriodoPlanilla" vli_value_source="UseItemData" vli_codfld="int" vli_enable_caching="0" vli_caching_expiration="0" vli_caching_expiration_mins="0" vli_definition="&lt;List&gt;&lt;Bound&gt;&#xA;    select ppl_codigo, convert(varchar, ppl_fecha_ini, 103) + '' - '' + convert(varchar, ppl_fecha_fin, 103) periodo from sal.ppl_periodos_planilla where ppl_codtpl = @codtpl and ppl_estado = ''Generado''&lt;/Bound&gt;&lt;/List&gt;" vli_usuario_grabacion="EVA" />
  <lista vli_id="PeriodosPlanillaGeneracionDeEmpleado" vli_description="Períodos de planilla en generación del tipo planilla del empleado enviado" vli_prompt="Periodo" vli_value_source="UseItemData" vli_codfld="int" vli_enable_caching="0" vli_caching_expiration="0" vli_caching_expiration_mins="0" vli_definition="&lt;List&gt;&lt;Bound&gt;&#xA;select ppl_codigo, convert(varchar, ppl_fecha_ini, 103) + '' - '' + convert(varchar, ppl_fecha_fin, 103) periodo &#xA;  from sal.ppl_periodos_planilla &#xA;  join sal.tpl_tipo_planilla on tpl_codigo = ppl_codtpl&#xA;  join exp.emp_empleos on emp_codtpl = tpl_codigo&#xA;  where ppl_estado = ''Generado'' and emp_codigo = @codemp&#xA;&lt;/Bound&gt;&lt;/List&gt;" vli_usuario_grabacion="EVA" />
  <lista vli_id="PeriodosPlanillasPendientes" vli_description="Lista de Períodos de Planillas Pendientes, Ordenado por Tipo de Planilla (Usa Código APP)" vli_prompt="PeriodosPlaPend" vli_value_source="UseSqlExpressionInItemData" vli_codfld="int" vli_enable_caching="0" vli_caching_expiration="0" vli_caching_expiration_mins="0" vli_definition="&lt;List&gt;&lt;Bound&gt;&#xA;    select ppl_codigo, convert(varchar, ppl_fecha_ini, 103) + '' - '' + convert(varchar, ppl_fecha_fin, 103) + '' '' + tpl_descripcion  periodo from sal.ppl_periodos_planilla inner join sal.tpl_tipo_planilla on tpl_codigo = ppl_codtpl where tpl_codcia = $$CODCIA$$ and ppl_estado = ''Generado''&#xA;Order by tpl_descripcion, ppl_fecha_ini&#xA;  &lt;/Bound&gt;&lt;/List&gt;" vli_usuario_grabacion="EVA" />
  <lista vli_id="PeriodosPptoActivosDeCompania" vli_description="Períodos Activos del presupuesto por compañía" vli_value_source="UseSqlExpressionInItemData" vli_codfld="int" vli_enable_caching="0" vli_caching_expiration="0" vli_caching_expiration_mins="0" vli_definition="&lt;List&gt;&lt;Bound&gt;&#xA;select pep_codigo, tip_nombre + '' - '' +  convert(varchar, pep_anio) + '' '' + convert(varchar, pep_version) periodo &#xA;from pto.pep_periodos&#xA;  join pto.tip_tipos_presupuesto on tip_codigo=pep_codtip&#xA;where tip_codcia=$$CODCIA$$&#xA;  and exists (select null from sco.permiso_compania_tabla(''$$USER$$'') where tip_codcia = codcia)&#xA;  and pep_estado = ''Formulacion''&#xA;order by pep_anio desc, pep_version desc&#xA;  &lt;/Bound&gt;&lt;/List&gt;" vli_usuario_grabacion="EVA" />
  <lista vli_id="PeriodosPptoActivosPorUsuario" vli_description="Lista los períodos del presupuesto en estado formulación que tenga acceso el usuario" vli_value_source="UseSqlExpressionInItemData" vli_codfld="int" vli_enable_caching="0" vli_caching_expiration="0" vli_caching_expiration_mins="0" vli_definition="&lt;List&gt;&lt;Bound&gt;&#xA;select pep_codigo, tip_nombre + '' - '' + convert(varchar, pep_anio) + '' '' + convert(varchar, pep_version) periodo&#xA;from pto.pep_periodos&#xA;  join pto.tip_tipos_presupuesto on tip_codigo=pep_codtip&#xA;where pep_estado=''Formulacion''&#xA;  and exists (  select 1 &#xA;          from sve.rpu_rubros_ppto_usuario join pto.rup_rubros on rup_codigo=rpu_codrup&#xA;          where rpu_codusr in (select usr_codigo from sec.usr_users where usr_username=''$$USER$$'') &#xA;            and rup_codpep=pep_codigo)&#xA;&#xA;  &lt;/Bound&gt;&lt;/List&gt;" vli_usuario_grabacion="EVA" />
  <lista vli_id="PeriodosPptoAutorizadoOEjecucionXCompania" vli_description="Períodos del presupuesto que se encuentran en estado Autorizado o Ejecucion por compañía" vli_value_source="UseSqlExpressionInItemData" vli_codfld="int" vli_enable_caching="0" vli_caching_expiration="0" vli_caching_expiration_mins="0" vli_definition="&lt;List&gt;&lt;Bound&gt;&#xA;select pep_codigo,  tip_nombre + '' - '' + convert(varchar, pep_anio) + '' '' + convert(varchar, pep_version) + '' ('' + pep_estado + '')'' periodo &#xA;from pto.pep_periodos&#xA;  join pto.tip_tipos_presupuesto on tip_codigo=pep_codtip&#xA;where tip_codcia=$$CODCIA$$&#xA;  and exists (select null from sco.permiso_compania_tabla(''$$USER$$'') where tip_codcia = codcia)&#xA;  and (pep_estado = ''Autorizado'' or pep_estado = ''Ejecucion'' or pep_estado = ''Cerrado'')&#xA;order by pep_anio desc, pep_version desc&#xA;&lt;/Bound&gt;&lt;/List&gt;" vli_usuario_grabacion="EVA" />
  <lista vli_id="PeriodosPptoDeCompaniaParaCopia" vli_description="Períodos del presupuesto para ser copiados en el período del código enviado" vli_value_source="UseSqlExpressionInItemData" vli_codfld="int" vli_enable_caching="0" vli_caching_expiration="0" vli_caching_expiration_mins="0" vli_definition="&lt;List&gt;&lt;Bound&gt;&#xA; select pep_codigo, tip_nombre + '' - '' + convert(varchar, pep_anio) + '' '' + convert(varchar, pep_version) periodo &#xA;from pto.pep_periodos&#xA;  join pto.tip_tipos_presupuesto on tip_codigo=pep_codtip&#xA;where tip_codcia=$$CODCIA$$&#xA;  and exists (select null from sco.permiso_compania_tabla(''$$USER$$'') where tip_codcia = codcia)&#xA;  and pep_codigo!=@pep_codigo&#xA;  and pep_estado!=''Inactivo''&#xA;order by pep_anio desc, pep_version desc&#xA;  &lt;/Bound&gt;&lt;/List&gt;" vli_usuario_grabacion="EVA" />
  <lista vli_id="PeriodosPptoPorCompania" vli_description="Periodos del presupuesto por compañía" vli_value_source="UseSqlExpressionInItemData" vli_codfld="int" vli_enable_caching="0" vli_caching_expiration="0" vli_caching_expiration_mins="0" vli_definition="&lt;List&gt;&lt;Bound&gt;&#xA; select pep_codigo, tip_nombre + '' - '' + convert(varchar, pep_anio) + '' '' + convert(varchar, pep_version) periodo &#xA;from pto.pep_periodos&#xA;  join pto.tip_tipos_presupuesto on tip_codigo=pep_codtip&#xA;where tip_codcia=$$CODCIA$$&#xA;  and exists (select null from sco.permiso_compania_tabla(''$$USER$$'') where tip_codcia = codcia)&#xA;order by pep_anio desc, pep_version desc&#xA;  &lt;/Bound&gt;&lt;/List&gt;" vli_usuario_grabacion="EVA" />
  <lista vli_id="PermisosTrabajoPorCompania" vli_description="Todos los permisos de trabajo por compañía" vli_prompt="Permiso de Trabajo" vli_value_source="UseSqlExpressionInItemData" vli_codfld="int" vli_enable_caching="0" vli_caching_expiration="0" vli_caching_expiration_mins="0" vli_definition="&lt;List&gt;&lt;Bound&gt;&#xA;SELECT ptr_codigo, ptr_descripcion&#xA;FROM sso.ptr_permisos_trabajos&#xA;WHERE ptr_codcia = $$CODCIA$$&#xA;  AND exists (select null from sco.permiso_compania_tabla(''$$USER$$'') where ptr_codcia = codcia)&#xA;  AND NOT EXISTS (SELECT NULL&#xA;          FROM sso.ppr_permisos_procesos&#xA;          WHERE ppr_codptr = ptr_codigo&#xA;            AND ppr_codpro = @codpro) &#xA;  &lt;/Bound&gt;&lt;/List&gt;" vli_usuario_grabacion="EVA" />
  <lista vli_id="PersonalMedicoPorPais" vli_description="Lista el personal médico por país" vli_value_source="UseSqlExpressionInItemData" vli_codfld="int" vli_enable_caching="0" vli_caching_expiration="0" vli_caching_expiration_mins="0" vli_definition="&lt;List&gt;&lt;Bound&gt;&#xA;select pem_codigo, isnull(exp_nombres_apellidos, pem_nombre) + '' ('' + isnull(pem_especialidad, '''') + '')'' nombre&#xA;from cli.pem_personal_medico&#xA;left join &#xA;  (exp.emp_empleos join exp.exp_expedientes on emp_codexp=exp_codigo) on emp_codigo=pem_codemp&#xA;where pem_codpai = (select cia_codpai from eor.cia_companias where cia_codigo = $$CODCIA$$) order by nombre&#xA;  &lt;/Bound&gt;&lt;/List&gt;" vli_usuario_grabacion="EVA" />
  <lista vli_id="PlanesActivosDeGrupoEnviado" vli_description="Lista los planes activos del grupo corporativo enviado" vli_value_source="UseItemData" vli_codfld="int" vli_enable_caching="0" vli_caching_expiration="0" vli_caching_expiration_mins="0" vli_definition="&lt;List&gt;&lt;Bound&gt;&#xA;  select pln_codigo, pln_nombre from cap.pln_planes where pln_codgrc = @codgrc and pln_estado = ''Activo'' order by pln_nombre&#xA;  &lt;/Bound&gt;&lt;/List&gt;" vli_usuario_grabacion="EVA" />
  <lista vli_id="PlantillasWord" vli_description="Lista las plantillas de word" vli_value_source="UseItemData" vli_codfld="int" vli_enable_caching="0" vli_caching_expiration="0" vli_caching_expiration_mins="0" vli_definition="&lt;List&gt;&lt;Bound&gt;&#xA;select wrd_codigo, wrd_nombre_loc_key&#xA;  from wrp.wrd_word_templates&#xA; order by wrd_codigo&#xA;  &lt;/Bound&gt;&lt;/List&gt;" vli_usuario_grabacion="EVA" />
  <lista vli_id="PlazasAutorizadasDelGrupoSeleccionado" vli_description="Lista las plazas autorizadas del grupo corporativo seleccionado" vli_prompt="Seleccionar" vli_value_source="UseItemData" vli_codfld="int" vli_enable_caching="0" vli_caching_expiration="0" vli_caching_expiration_mins="0" vli_definition="&lt;List&gt;&lt;Bound&gt;&#xA;    select plz_codigo, plz_nombre from eor.plz_plazas join eor.cia_companias on cia_codigo = plz_codcia where cia_codgrc = $$CODGRC$$  and exists (select null from sco.permiso_unidad_tabla(''$$USER$$'') where plz_coduni = coduni)&#xA;  &lt;/Bound&gt;&lt;/List&gt;" vli_usuario_grabacion="EVA" />
  <lista vli_id="PlazasAutVacantesDeCompaniaYPuestoEnviado" vli_description="Lista las plazas autorizadas vacantes de la compañía y puesto enviado" vli_value_source="UseItemData" vli_codfld="int" vli_enable_caching="0" vli_caching_expiration="0" vli_caching_expiration_mins="0" vli_definition="&lt;List&gt;&lt;Bound&gt;&#xA;    select plz_codigo, plz_nombre from eor.plz_plazas_vacantes_v &#xA;  join eor.cia_companias on cia_codigo = plz_codcia&#xA; where exists (select null from sco.permiso_unidad_tabla(''$$USER$$'') where plz_coduni = coduni) and plz_codcia = SUBSTRING(''@codpue_codcia'', CHARINDEX(''|'', ''@codpue_codcia'', 0) + 1, LEN(''@codpue_codcia'') - CHARINDEX(''|'', ''@codpue_codcia'', 0)) and plz_codpue = SUBSTRING(''@codpue_codcia'', 1, CHARINDEX(''|'', ''@codpue_codcia'', 0) - 1)&#xA;  &lt;/Bound&gt;&lt;/List&gt;" vli_usuario_grabacion="EVA" />
  <lista vli_id="PlazasAutVigentesConEmpActivosDeCiaSeleccionada" vli_description="Muestra las plazas autorizadas vigentes con empleos activos de la compañía seleccionada" vli_value_source="UseItemData" vli_codfld="int" vli_enable_caching="0" vli_caching_expiration="0" vli_caching_expiration_mins="0" vli_definition="&lt;List&gt;&lt;Bound&gt;&#xA;      select plz_codigo, plz_nombre&#xA;        from eor.plz_plazas&#xA;        join eor.cia_companias on cia_codigo = plz_codcia&#xA;       where exists(select null from exp.emp_empleos where emp_estado = ''A'' and plz_codigo = emp_codplz)&#xA;         and plz_estado = ''V''&#xA;         and cia_codigo = $$CODCIA$$&#xA;         and exists (select null from sco.permiso_plaza_tabla(''$$USER$$'') where plz_codigo = codplz)&#xA;  &lt;/Bound&gt;&lt;/List&gt;" vli_usuario_grabacion="EVA" />
  <lista vli_id="PlazasDelGrupoCorporativo" vli_description="Lista las plazas del grupo corporativo" vli_prompt="" vli_value_source="UseItemData" vli_codfld="int" vli_enable_caching="0" vli_caching_expiration="0" vli_caching_expiration_mins="1" vli_definition="&lt;List&gt;&lt;Bound&gt;&#xA;    select plz_codigo, plz_nombre from eor.plz_plazas join eor.cia_companias on cia_codigo = plz_codcia where cia_codgrc = $$CODGRC$$&#xA;  &lt;/Bound&gt;&lt;/List&gt;" vli_usuario_grabacion="EVA" />
  <lista vli_id="PlazasPermitidasPorJerarquia" vli_description="Lista las plazas permitidas para un usuario que son inferior al empleo enviado" vli_prompt="Plazas" vli_value_source="UseItemData" vli_codfld="int" vli_enable_caching="0" vli_caching_expiration="0" vli_caching_expiration_mins="0" vli_definition="&lt;List&gt;&lt;Bound&gt;&#xA;    declare @@cod_plz int&#xA;select @@cod_plz = jefe.plz_codigo from eor.plz_plazas jefe join exp.emp_empleos on emp_codplz = jefe.plz_codigo where emp_codigo = @codemp&#xA;select plz_codigo, plz_nombre from  eor.plz_plazas join eor.fn_retorna_plazas_hijas(@@cod_plz) on codplz = plz_codigo where exists (select null from sco.permiso_unidad_tabla(''$$USER$$'') where plz_coduni = coduni) order by plz_nombre&#xA;  &lt;/Bound&gt;&lt;/List&gt;" vli_usuario_grabacion="EVA" />
  <lista vli_id="PlazasPptoDelPeriodoPptoEnviado" vli_description="Listado de plazas del presupuesto del período del presupuesto enviado" vli_value_source="UseSqlExpressionInItemData" vli_codfld="int" vli_enable_caching="0" vli_caching_expiration="0" vli_caching_expiration_mins="0" vli_definition="&lt;List&gt;&lt;Bound&gt;&#xA;select pzp_codigo, pzp_nombre &#xA;from pto.pzp_plazas &#xA;left join pto.emo_empleos ON emo_codpzp=pzp_codigo AND emo_estado=''A''&#xA;where pzp_codpep = @codpep&#xA;and( ISNULL([gen].[get_valor_parametro_bit](''PptoCalculoSoloPlazasConEmpleos'', null, null, $$CODCIA$$, null), 0) = 0&#xA;or ( ISNULL([gen].[get_valor_parametro_bit](''PptoCalculoSoloPlazasConEmpleos'', null, null, $$CODCIA$$, null), 0) = 1&#xA;  AND emo_codigo IS NOT NULL&#xA;    )&#xA;)&#xA;&lt;/Bound&gt;&lt;/List&gt;" vli_usuario_grabacion="EVA" />
  <lista vli_id="PlazasPptoDelPeriodoPptoEnviadoPortal" vli_description="Listado de plazas del presupuesto del período del presupuesto enviado (diseñado para portal)" vli_value_source="UseSqlExpressionInItemData" vli_codfld="int" vli_enable_caching="0" vli_caching_expiration="0" vli_caching_expiration_mins="0" vli_definition="&lt;List&gt;&lt;Bound&gt;&#xA;select pzp_codigo, ''('' + CONVERT(VARCHAR, pzp_codigo) + '') '' + pzp_nombre nombre&#xA;from pto.pzp_plazas&#xA;  join pto.unp_unidades on unp_codigo=pzp_codunp&#xA;  left join pto.emo_empleos ON emo_codpzp=pzp_codigo AND emo_estado=''A''&#xA;where pzp_codpep=@codpep&#xA;and (&#xA;    exists (select 1 from sco.permiso_plaza_tabla(''$$USER$$'') where codplz=pzp_codplz_ref)&#xA;    or (&#xA;      exists (select 1 from sco.permiso_unidad_tabla(''$$USER$$'') where coduni=unp_coduni_ref)&#xA;      and pzp_codplz_ref is null&#xA;      )&#xA;  )&#xA;and( ISNULL([gen].[get_valor_parametro_bit](''PptoCalculoSoloPlazasConEmpleos'', null, null, $$CODCIA$$, null), 0) = 0&#xA;or ( ISNULL([gen].[get_valor_parametro_bit](''PptoCalculoSoloPlazasConEmpleos'', null, null, $$CODCIA$$, null), 0) = 1&#xA;  AND emo_codigo IS NOT NULL&#xA;    )&#xA;)&#xA;  &lt;/Bound&gt;&lt;/List&gt;" vli_usuario_grabacion="EVA" />
  <lista vli_id="PlazasVacantesDeConcursoYCompaniaEnviado" vli_description="Lista las plazas vacantes de la compañía y concurso enviado" vli_value_source="UseItemData" vli_codfld="int" vli_enable_caching="0" vli_caching_expiration="0" vli_caching_expiration_mins="0" vli_definition="&lt;List&gt;&lt;Bound&gt;&#xA;    select distinct plz_codigo, plz_nombre from rys.pvc_plazas_vac_concurso&#xA;join eor.plz_plazas on plz_codigo = pvc_codplz_vacante&#xA;where plz_codcia = SUBSTRING(''@codcos_codcia'', CHARINDEX(''|'', ''@codcos_codcia'', 0) + 1, LEN(''@codcos_codcia'') - CHARINDEX(''|'', ''@codcos_codcia'', 0)) and &#xA;      pvc_codcos = SUBSTRING(''@codcos_codcia'', 1, CHARINDEX(''|'', ''@codcos_codcia'', 0) - 1)&#xA;  &lt;/Bound&gt;&lt;/List&gt;" vli_usuario_grabacion="EVA" />
  <lista vli_id="PlazasVigentesAutDeGrupoEnviadoExcluyCiaEnviada" vli_description="Lista las plazas vigentes autorizadas del grupo enviado excluyendo las que pertenecen a la compañía enviada" vli_value_source="UseItemData" vli_codfld="int" vli_enable_caching="0" vli_caching_expiration="0" vli_caching_expiration_mins="0" vli_definition="&lt;List&gt;&lt;Bound&gt;&#xA;  select plz_codigo, plz_nombre&#xA;      from eor.plz_plazas&#xA;      join eor.cia_companias on cia_codigo = plz_codcia&#xA;     where cia_codgrc = @codgrc and exists (select null from sco.permiso_plaza_tabla(''$$USER$$'') where plz_codigo = codplz)&#xA;     and plz_estado = ''V''&#xA;&#x9; and plz_codcia != @codcia&#xA;order by plz_nombre&#xA;  &lt;/Bound&gt;&lt;/List&gt;" vli_usuario_grabacion="EVA" />
  <lista vli_id="PlazasVigentesAutorizadasDeCompaniaEnviado" vli_description="Lista las plazas vigentes autorizadas de la compañía enviado" vli_value_source="UseItemData" vli_codfld="int" vli_enable_caching="0" vli_caching_expiration="0" vli_caching_expiration_mins="0" vli_definition="&lt;List&gt;&lt;Bound&gt;&#xA;  select plz_codigo, plz_nombre&#xA;      from eor.plz_plazas&#xA;     where plz_codcia = @codcia and exists (select null from sco.permiso_plaza_tabla(''$$USER$$'') where plz_codigo = codplz)&#xA;     and plz_estado = ''V'' order by plz_nombre&#xA;  &lt;/Bound&gt;&lt;/List&gt;" vli_usuario_grabacion="EVA" />
  <lista vli_id="PlazasVigentesAutorizadasDeGrupoEnviado" vli_description="Lista las plazas vigentes autorizadas del grupo enviado" vli_value_source="UseItemData" vli_codfld="int" vli_enable_caching="0" vli_caching_expiration="0" vli_caching_expiration_mins="0" vli_definition="&lt;List&gt;&lt;Bound&gt;&#xA;  select plz_codigo, plz_nombre&#xA;      from eor.plz_plazas&#xA;      join eor.cia_companias on cia_codigo = plz_codcia&#xA;     where cia_codgrc = @codgrc and exists (select null from sco.permiso_plaza_tabla(''$$USER$$'') where plz_codigo = codplz)&#xA;     and plz_estado = ''V''&#xA;  &lt;/Bound&gt;&lt;/List&gt;" vli_usuario_grabacion="EVA" />
