  <lista vli_id="PlazasVigentesDelGrupoCorporativo" vli_description="Listado de Plazas Vigentes del Grupo Corporativo Seleccionado" vli_value_source="UseItemData" vli_codfld="int" vli_enable_caching="0" vli_caching_expiration="0" vli_caching_expiration_mins="1" vli_definition="&lt;List&gt;&lt;Bound&gt;&#xA;select plz_codigo, plz_nombre + '' ({Vacante})''&#xA;  from eor.plz_plazas &#xA;  join eor.cia_companias on cia_codigo = plz_codcia &#xA; where cia_codgrc = $$CODGRC$$&#xA;   and plz_estado = ''V''&#xA;   and not exists (select null from exp.emp_empleos where emp_codplz = plz_codigo and emp_estado = ''A'')&#xA;union all&#xA;select plz_codigo, plz_nombre + '' ('' + exp_nombres_apellidos + '')''&#xA;  from eor.plz_plazas &#xA;  join eor.cia_companias on cia_codigo = plz_codcia &#xA;  join exp.emp_empleos on emp_codplz = plz_codigo&#xA;  join exp.exp_expedientes on exp_codigo = emp_codexp&#xA; where cia_codgrc = $$CODGRC$$&#xA;   and plz_estado = ''V''&#xA;   and (select count(*) from exp.emp_empleos where emp_codplz = plz_codigo and emp_estado = ''A'') = 1&#xA;union all&#xA;select plz_codigo, plz_nombre + '' ('' + cast((select count(*) from exp.emp_empleos where emp_codplz = plz_codigo and emp_estado = ''A'') as varchar) + lower('' {Empleados})'')&#xA;  from eor.plz_plazas &#xA;  join eor.cia_companias on cia_codigo = plz_codcia &#xA; where cia_codgrc = $$CODGRC$$&#xA;   and plz_estado = ''V''&#xA;   and (select count(*) from exp.emp_empleos where emp_codplz = plz_codigo and emp_estado = ''A'') &amp;gt; 1&#xA; order by 2    &#xA;  &lt;/Bound&gt;&lt;/List&gt;" vli_usuario_grabacion="EVA" />
  <lista vli_id="PreguntasDeDimensionEnviada" vli_description="Muestra las preguntas asociada a una dimensión especificado" vli_value_source="UseItemData" vli_codfld="int" vli_enable_caching="0" vli_caching_expiration="0" vli_caching_expiration_mins="0" vli_definition="&lt;List&gt;&lt;Bound&gt;&#xA;    select pre_codigo, pre_enunciado from cor.pre_preguntas where pre_coddim = @coddim&#xA;  &lt;/Bound&gt;&lt;/List&gt;" vli_usuario_grabacion="EVA" />
  <lista vli_id="PrestacionesDeGrupoCorporativo" vli_description="Lista las prestaciones de un grupo corporativo" vli_prompt="" vli_value_source="UseItemData" vli_codfld="int" vli_enable_caching="0" vli_caching_expiration="0" vli_caching_expiration_mins="1" vli_definition="&lt;List&gt;&lt;Bound&gt;&#xA;    select pre_codigo, pre_descripcion + '' ('' + cia_descripcion + '')'' pre_descripcion from eor.pre_prestaciones join eor.cia_companias on cia_codigo = pre_codcia where cia_codgrc = $$CODGRC$$ order by cia_descripcion, pre_descripcion&#xA;  &lt;/Bound&gt;&lt;/List&gt;" vli_usuario_grabacion="EVA" />
