  <lista vli_id="PrestacionesPptoPorCompania" vli_description="Prestaciones del presupuesto por empresa" vli_value_source="UseSqlExpressionInItemData" vli_codfld="int" vli_enable_caching="0" vli_caching_expiration="0" vli_caching_expiration_mins="0" vli_definition="&lt;List&gt;&lt;Bound&gt;&#xA;select&#xA;prp_codigo,&#xA;prp_descripcion&#xA;from pto.prp_prestaciones&#xA;where prp_codcia=$$CODCIA$$&#xA;  &lt;/Bound&gt;&lt;/List&gt;" vli_usuario_grabacion="EVA" />
  <lista vli_id="PresupuestosAutorizadosDeGrupoEnviado" vli_description="Lista los presupuestos autorizados del grupo enviado" vli_value_source="UseItemData" vli_codfld="int" vli_enable_caching="0" vli_caching_expiration="0" vli_caching_expiration_mins="0" vli_definition="&lt;List&gt;&lt;Bound&gt;&#xA;  select prs_codigo, prs_nombre from cap.prs_presupuestos where prs_codgrc = @codgrc and prs_estado = ''Autorizado'' and prs_cerrado = 0 order by prs_nombre&#xA;  &lt;/Bound&gt;&lt;/List&gt;" vli_usuario_grabacion="EVA" />
  <lista vli_id="ProcesosConActividadPorCompania" vli_description="Procesos que tienen actividad por compañía" vli_prompt="Seleccionar Proceso" vli_value_source="UseItemData" vli_codfld="int" vli_enable_caching="0" vli_caching_expiration="0" vli_caching_expiration_mins="0" vli_definition="&lt;List&gt;&lt;Bound&gt;&#xA;select pro_codigo, pro_nombre from sso.pro_procesos join sso.adt_areas_de_trabajo on pro_codadt=adt_codigo join eor.cdt_centros_de_trabajo on adt_codcdt=cdt_codigo where pro_estado=''Vigente'' and cdt_codcia = $$CODCIA$$ and exists (select 1 from sso.act_actividades where act_codpro=pro_codigo and act_estado=''Vigente'') and exists (select null from sco.permiso_compania_tabla(''$$USER$$'') where cdt_codcia = codcia)&#xA;  &lt;/Bound&gt;&lt;/List&gt;" vli_usuario_grabacion="EVA" />
  <lista vli_id="ProcesosEmpresaHijoPorAreaDeTrabajo" vli_description="Muestra los procesos vigentes de empresa del nivel mas bajo por área de trabajo, aplica si sus hijos están suprimidos" vli_prompt="Seleccionar" vli_value_source="UseItemData" vli_codfld="int" vli_enable_caching="0" vli_caching_expiration="0" vli_caching_expiration_mins="0" vli_definition="&lt;List&gt;&lt;Bound&gt;&#xA;select pro_codigo, pro_nombre&#xA;from sso.pro_procesos p1 &#xA;join sso.adt_areas_de_trabajo on pro_codadt=adt_codigo &#xA;join eor.cdt_centros_de_trabajo on adt_codcdt=cdt_codigo &#xA;where cdt_codcia = $$CODCIA$$ &#xA;  and exists (select null from sco.permiso_compania_tabla(''$$USER$$'') where cdt_codcia = codcia) &#xA;  and pro_estado=''Vigente'' &#xA;  and not exists( select 1 from sso.pro_procesos p2 where p1.pro_codigo = p2.pro_padre and p2.pro_estado=''Vigente'')&#xA;  and adt_codigo=@adt_codigo&#xA;&lt;/Bound&gt;&lt;/List&gt;" vli_usuario_grabacion="EVA" />
  <lista vli_id="ProcesosEmpresaHijoPorCompania" vli_description="Muestra los procesos vigentes de empresa del nivel mas bajo por compañía, aplica si sus hijos están suprimidos" vli_prompt="Seleccionar" vli_value_source="UseItemData" vli_codfld="int" vli_enable_caching="0" vli_caching_expiration="0" vli_caching_expiration_mins="0" vli_definition="&lt;List&gt;&lt;Bound&gt;&#xA;select pro_codigo, pro_nombre&#xA;from sso.pro_procesos p1 &#xA;join sso.adt_areas_de_trabajo on pro_codadt=adt_codigo &#xA;join eor.cdt_centros_de_trabajo on adt_codcdt=cdt_codigo &#xA;where cdt_codcia = $$CODCIA$$ &#xA;  and exists (select null from sco.permiso_compania_tabla(''$$USER$$'') where cdt_codcia = codcia) &#xA;  and pro_estado=''Vigente'' &#xA;  and not exists( select 1 from sso.pro_procesos p2 where p1.pro_codigo = p2.pro_padre and p2.pro_estado=''Vigente'') &#xA;&lt;/Bound&gt;&lt;/List&gt;" vli_usuario_grabacion="EVA" />
  <lista vli_id="ProcesosEmpresaPorCompania" vli_description="Muestra todos los procesos vigentes de la compañía" vli_prompt="Seleccionar" vli_value_source="UseItemData" vli_codfld="int" vli_enable_caching="0" vli_caching_expiration="0" vli_caching_expiration_mins="0" vli_definition="&lt;List&gt;&lt;Bound&gt;     select pro_codigo, pro_nombre from sso.pro_procesos join sso.adt_areas_de_trabajo on pro_codadt=adt_codigo join eor.cdt_centros_de_trabajo on adt_codcdt=cdt_codigo where cdt_codcia = $$CODCIA$$   and exists (select null from sco.permiso_compania_tabla(''$$USER$$'') where cdt_codcia = codcia) and pro_estado=''Vigente'' &lt;/Bound&gt;&lt;/List&gt;" vli_usuario_grabacion="EVA" />
  <lista vli_id="ProcesosParaPadrePorCompania" vli_description="Todos los procesos que pueden tener subprocesos, no deben de tener ninguna actividad, herramienta especial, requisito de emergencia, permiso de trabajo, producto o elemento especial asociado" vli_prompt="Proceso Padre" vli_value_source="UseSqlExpressionInItemData" vli_codfld="int" vli_enable_caching="0" vli_caching_expiration="0" vli_caching_expiration_mins="0" vli_definition="&lt;List&gt;&lt;Bound&gt;&#xA;SELECT pro_codigo, pro_nombre&#xA;FROM sso.pro_procesos&#xA;  JOIN sso.adt_areas_de_trabajo ON pro_codadt = adt_codigo&#xA;  JOIN eor.cdt_centros_de_trabajo ON adt_codcdt = cdt_codigo&#xA;WHERE cdt_codcia = $$CODCIA$$&#xA;  AND exists (select null from sco.permiso_compania_tabla(''$$USER$$'') where cdt_codcia = codcia)&#xA;  AND pro_codigo != @codpro&#xA;  AND pro_estado = ''Vigente''&#xA;  AND NOT EXISTS (SELECT NULL&#xA;      FROM sso.pro_procesos&#xA;      WHERE pro_padre = @codpro)&#xA;  AND NOT EXISTS (SELECT NULL&#xA;          FROM sso.ppr_permisos_procesos&#xA;          WHERE ppr_codpro = pro_codigo)&#xA;  AND NOT EXISTS (SELECT NULL&#xA;          FROM sso.hpr_herramientas_procesos&#xA;          WHERE hpr_codpro = pro_codigo)&#xA;  AND NOT EXISTS (SELECT NULL&#xA;          FROM sso.rpr_requisitos_procesos&#xA;          WHERE rpr_codpro = pro_codigo)&#xA;  AND NOT EXISTS (SELECT NULL&#xA;          FROM sso.ppr_productos_procesos&#xA;          WHERE ppr_codpro = pro_codigo)          &#xA;  AND NOT EXISTS (SELECT NULL&#xA;          FROM sso.epr_elementos_procesos&#xA;          WHERE epr_codpro = pro_codigo)          &#xA;  AND NOT EXISTS (SELECT NULL&#xA;          FROM sso.act_actividades&#xA;          WHERE act_codpro = pro_codigo)&#xA;  &lt;/Bound&gt;&lt;/List&gt;" vli_usuario_grabacion="EVA" />
  <lista vli_id="ProductoPorCompaniaParaProceso" vli_description="Todos los productos por compañía que no esten en determinado proceso" vli_prompt="Producto" vli_value_source="UseSqlExpressionInItemData" vli_codfld="string" vli_enable_caching="0" vli_caching_expiration="0" vli_caching_expiration_mins="0" vli_definition="&lt;List&gt;&lt;Bound&gt;&#xA;SELECT prd_codigo_visual, prd_nombre&#xA;FROM sso.prd_productos&#xA;WHERE prd_codcia = $$CODCIA$$&#xA;  AND exists (select null from sco.permiso_compania_tabla(''$$USER$$'') where prd_codcia = codcia)&#xA;  AND NOT EXISTS (SELECT NULL&#xA;          FROM sso.ppr_productos_procesos&#xA;          WHERE ppr_codprd = prd_codigo&#xA;            AND ppr_codpro = @codpro)&#xA;  &lt;/Bound&gt;&lt;/List&gt;" vli_usuario_grabacion="EVA" />
