WITH src AS (
SELECT  
xt.xc.value('@vli_id', 'varchar(50)') AS vli_id
, xt.xc.value('@vli_description', 'varchar(255)') AS vli_description
, xt.xc.value('@vli_prompt', 'varchar(50)') AS vli_prompt
, xt.xc.value('@vli_value_source', 'varchar(50)') AS vli_value_source
, xt.xc.value('@vli_codfld', 'varchar(50)') AS vli_codfld
, xt.xc.value('@vli_enable_caching', 'bit') AS vli_enable_caching
, xt.xc.value('@vli_caching_expiration', 'tinyint') AS vli_caching_expiration
, xt.xc.value('@vli_caching_expiration_mins', 'int') AS vli_caching_expiration_mins
, xt.xc.value('@vli_definition', 'varchar(MAX)') AS vli_definition
, xt.xc.value('@vli_usuario_grabacion', 'varchar(50)') AS vli_usuario_grabacion
 FROM @vliXML.nodes('/listas/lista') AS xt(xc)
) MERGE INTO cfg.vli_value_lists dest
	USING src ON src.vli_id = dest.vli_id
		WHEN NOT MATCHED THEN
			INSERT (	vli_id
						,vli_description
						,vli_prompt
						,vli_value_source
						,vli_codfld
						,vli_enable_caching
						,vli_caching_expiration
						,vli_caching_expiration_mins
						,vli_definition
						,vli_usuario_grabacion
					) VALUES (
						src.vli_id
						,src.vli_description
						,src.vli_prompt
						,src.vli_value_source
						,src.vli_codfld
						,src.vli_enable_caching
						,src.vli_caching_expiration
						,src.vli_caching_expiration_mins
						,CAST(src.vli_definition AS XML)
						,src.vli_usuario_grabacion
					);

WITH src AS (
SELECT  
xt.xc.value('@pbf_entidad', 'varchar(100)') AS pbf_entidad
, xt.xc.value('@pbf_field_name', 'varchar(50)') AS pbf_field_name
, xt.xc.value('@pbf_prompt_loc_key', 'varchar(100)') AS pbf_prompt_loc_key
, xt.xc.value('@pbf_descripcion_loc_key', 'varchar(200)') AS pbf_descripcion_loc_key
, xt.xc.value('@pbf_codfld', 'varchar(50)') AS pbf_codfld
, xt.xc.value('@pbf_codvli', 'varchar(50)') AS pbf_codvli
, xt.xc.value('@pbf_list_mode', 'varchar(20)') AS pbf_list_mode
, xt.xc.value('@pbf_group_loc_key', 'varchar(100)') AS pbf_group_loc_key
, xt.xc.value('@pbf_controller_action_name', 'varchar(100)') AS pbf_controller_action_name
, xt.xc.value('@pbf_orden', 'int') AS pbf_orden
, xt.xc.value('@pbf_usuario_grabacion', 'varchar(50)') AS pbf_usuario_grabacion
 FROM @pbfXML.nodes('/property_bags/property_bag') AS xt(xc)
) MERGE INTO cfg.pbf_property_bag_fields dest
	USING src ON src.pbf_field_name = dest.pbf_field_name AND src.pbf_entidad = dest.pbf_entidad
		WHEN MATCHED THEN
			UPDATE SET	pbf_group_loc_key = src.pbf_group_loc_key
		WHEN NOT MATCHED THEN
			INSERT (	pbf_entidad
						,pbf_field_name
						,pbf_prompt_loc_key
						,pbf_descripcion_loc_key
						,pbf_codfld
						,pbf_codvli
						,pbf_list_mode
						,pbf_group_loc_key
						,pbf_controller_action_name
						,pbf_orden
						,pbf_usuario_grabacion
					) VALUES (
						src.pbf_entidad
						,src.pbf_field_name
						,src.pbf_prompt_loc_key
						,src.pbf_descripcion_loc_key
						,src.pbf_codfld
						,src.pbf_codvli
						,src.pbf_list_mode
						,src.pbf_group_loc_key
						,src.pbf_controller_action_name
						,src.pbf_orden
						,src.pbf_usuario_grabacion
					);

COMMIT TRANSACTION;
