SET @fcuXML = '<cursores>
  <cursor fcu_codpai="pa" fcu_proceso="Planilla" fcu_nombre="AnticipoXIII_Pagado" fcu_descripcion="AnticipoXIII_Pagado" fcu_select_edit="SELECT 0 INN_CODCIA, /*INN_CODTPL, INN_CODPLA, */ INN_CODEMP, 0.00 ADELANTO_XIII, 0.00 ADELANTO_XIII_GR&#xD;&#xA;FROM sal.inn_ingresos&#xD;&#xA;WHERE inn_codigo &lt; 0" fcu_select_run="DECLARE @codcia INT, @codtpl INT, @codppl INT, @fecha_ini DATETIME, @fecha_fin DATETIME&#xD;&#xA;DECLARE @codtpl_anticipo INT, @codtig_anticipoXIII INT, @codtig_anticipoXIII_GR INT&#xD;&#xA;&#xD;&#xA;set @codcia = $$CODCIA$$&#xD;&#xA;set @codtpl = $$CODTPL$$&#xD;&#xA;set @codppl = $$CODPPL$$&#xD;&#xA;&#xD;&#xA;SET @codtpl_anticipo = 6 -- Planilla de anticipo XIII&#xD;&#xA;SELECT @codtig_anticipoXIII = tig_codigo FROM sal.tig_tipos_ingreso where tig_codcia=@codcia and tig_abreviatura=''ADELANTO_XIII'' -- Ingreso de anticipo XIII&#xD;&#xA;SELECT @codtig_anticipoXIII_GR = tig_codigo FROM sal.tig_tipos_ingreso where tig_codcia=@codcia and tig_abreviatura=''ADELANTO_XIII_GR'' -- Ingreso de anticipo XIII Gasto de Representacion&#xD;&#xA;&#xD;&#xA;SELECT @fecha_ini = PPL_FECHA_INI, @fecha_fin = PPL_FECHA_FIN&#xD;&#xA;FROM sal.ppl_periodos_planilla&#xD;&#xA;JOIN sal.tpl_tipo_planilla ON tpl_codigo = ppl_codtpl&#xD;&#xA;WHERE tpl_codcia = @codcia&#xD;&#xA;AND PPL_CODTPL = @codtpl&#xD;&#xA;AND ppl_codigo = @codppl&#xD;&#xA;&#xD;&#xA;SELECT tpl_codcia INN_CODCIA, /*INN_CODTPL, INN_CODPLA, */ INN_CODEMP, &#xD;&#xA;       SUM(CASE INN_CODTIG WHEN @codtig_anticipoXIII THEN INN_VALOR ELSE 0 END) ADELANTO_XIII,&#xD;&#xA;       SUM(CASE INN_CODTIG WHEN @codtig_anticipoXIII_GR THEN INN_VALOR ELSE 0 END) ADELANTO_XIII_GR&#xD;&#xA;FROM sal.inn_ingresos&#xD;&#xA;JOIN sal.ppl_periodos_planilla&#xD;&#xA;ON ppl_codigo = inn_codppl&#xD;&#xA;JOIN sal.tpl_tipo_planilla&#xD;&#xA;ON tpl_codigo = ppl_codtpl&#xD;&#xA;WHERE PPL_CODTPL = @codtpl_anticipo&#xD;&#xA;AND PPL_FECHA_FIN &gt;= @fecha_ini&#xD;&#xA;AND PPL_FECHA_FIN &lt;= @fecha_fin&#xD;&#xA;AND INN_VALOR &gt; 0&#xD;&#xA;AND PPL_ESTADO = ''Autorizado''&#xD;&#xA;GROUP BY tpl_codcia, /*INN_CODTPL, INN_CODPLA,*/ INN_CODEMP&#xD;&#xA;ORDER BY INN_CODCIA, /*INN_CODTPL, INN_CODPLA,*/ INN_CODEMP&#xD;&#xA;" fcu_field_codemp="inn_codemp" fcu_loop_calculo="0" fcu_updatable="0" fcu_modo_asociacion_tpl="TodosExcluyendo" />
  <cursor fcu_codpai="pa" fcu_proceso="Planilla" fcu_nombre="CuotasIngresosCiclicos" fcu_descripcion="CuotasIngresosCiclicos" fcu_select_edit="select *&#xD;&#xA;from sal.cic_cuotas_ingreso_ciclico&#xD;&#xA;join sal.igc_ingresos_ciclicos&#xD;&#xA;on cic_codigc = igc_codigo&#xD;&#xA;where 1 = 2" fcu_select_run="select *&#xD;&#xA;from sal.cic_cuotas_ingreso_ciclico&#xD;&#xA;join sal.igc_ingresos_ciclicos&#xD;&#xA;on cic_codigc = igc_codigo&#xD;&#xA;where cic_codppl = $$CODPPL$$&#xD;&#xA;--and sal.empleado_en_gen_planilla(''$$SESSIONID$$'', igc_codemp) = 1&#xD;&#xA;and cic_valor_cuota &gt; 0" fcu_field_codppl="cic_codppl" fcu_field_codemp="" fcu_loop_calculo="0" fcu_updatable="1" fcu_modo_asociacion_tpl="TodosExcluyendo" />
