  <cursor fcu_codpai="pa" fcu_proceso="Planilla" fcu_nombre="DescuentosEstaPlanilla" fcu_descripcion="Descuentos Aplicados en la Planilla" fcu_select_edit="select * from tmp.dss_descuentos&#xD;&#xA;where dss_codigo &lt; 0" fcu_select_run="select * &#xD;&#xA;from tmp.dss_descuentos&#xD;&#xA;where dss_codppl = $$CODPPL$$" fcu_field_codppl="dss_codppl" fcu_field_codemp="" fcu_loop_calculo="0" fcu_updatable="1" fcu_modo_asociacion_tpl="TodosExcluyendo" />
  <cursor fcu_codpai="pa" fcu_proceso="Planilla" fcu_nombre="DiasPagadosIncapacidad" fcu_descripcion="DiasPagadosIncapacidad" fcu_select_edit="SELECT 0 IXE_CODCIA, 0 IXE_CODEMP, 0 DIAS_PAGADOS" fcu_select_run="SET DATEFORMAT DMY&#xD;&#xA;SELECT txi_codcia IXE_CODCIA, IXE_CODEMP, COALESCE(SUM(PIE_DIAS), 0) DIAS_PAGADOS&#xD;&#xA;FROM acc.ixe_incapacidades&#xD;&#xA;JOIN acc.pie_periodos_incapacidad ON ixe_codigo = pie_codixe&#xD;&#xA;JOIN acc.txi_tipos_incapacidad ON txi_codigo = ixe_codtxi&#xD;&#xA;JOIN acc.rin_riesgos_incapacidades ON rin_codigo= ixe_codrin&#xD;&#xA;WHERE txi_codcia = $$CODCIA$$&#xD;&#xA;AND pie_aplicado_planilla = 1&#xD;&#xA;AND pie_planilla_autorizada = 1&#xD;&#xA;and rin_utiliza_fondo = 1&#xD;&#xA;AND PIE_INICIO &gt;= CONVERT(DATETIME, CONVERT(VARCHAR, DATEADD(YY, -2, GETDATE()), 103))&#xD;&#xA;AND pie_valor_a_pagar != 0&#xD;&#xA;GROUP BY txi_codcia, IXE_CODEMP&#xD;&#xA;" fcu_field_codcia="" fcu_field_codemp="IXE_CODEMP" fcu_loop_calculo="0" fcu_updatable="0" fcu_modo_asociacion_tpl="TodosExcluyendo" />
