  <lista vli_id="CategoriasMotivoRetiroDePaisDeCiaDeEmpleoEnviado" vli_description="Listas las categorias de motivo de retiro del país de la compañía del empleo enviado como parametro" vli_value_source="UseItemData" vli_codfld="int" vli_enable_caching="0" vli_caching_expiration="0" vli_caching_expiration_mins="0" vli_definition="&lt;List&gt;&lt;Bound&gt;&#xA;    select cmr_codigo, cmr_descripcion&#xA;      from exp.cmr_categorias_motivo_retiro&#xA;   where cmr_codpai = (select cia_codpai &#xA;                         from exp.emp_empleos &#xA;               join eor.plz_plazas on plz_codigo = emp_codplz&#xA;               join eor.cia_companias on cia_codigo = plz_codcia&#xA;              where emp_codigo = @codemp)&#xA;  order by cmr_descripcion&#xA;  &lt;/Bound&gt;&lt;/List&gt;" vli_usuario_grabacion="EVA" />
