  <lista vli_id="CentroCostoOmEx" vli_description="Centros de costoso que se mostraran en el omniexpediente" vli_value_source="UseItemData" vli_codfld="string" vli_enable_caching="0" vli_caching_expiration="0" vli_caching_expiration_mins="0" vli_definition="&lt;List&gt;&lt;Bound&gt;&#xA;  select cco_nomenclatura_contable, cco_descripcion from eor.cco_centros_de_costo where cco_codcia = $$CODCIA$$ and cco_estado = ''Vigente''&#xA; order by cco_descripcion&#xA;&lt;/Bound&gt;&lt;/List&gt;" vli_usuario_grabacion="EVA" />
