  <lista vli_id="CompetenciasParaActividad" vli_description="Lista las competencias por grupo corporativo en estado Vigente que no estén ya registradas en la actividad" vli_prompt="Seleccionar" vli_value_source="UseItemData" vli_codfld="int" vli_enable_caching="0" vli_caching_expiration="0" vli_caching_expiration_mins="0" vli_definition="&lt;List&gt;&lt;Bound&gt; select com_codigo, com_descripcion from eor.com_competencias where com_estado = ''Vigente''  and exists (select null from sco.permiso_grupo_tabla(''$$USER$$'') where com_codgrc = codgrc) and not exists (select 1 from sso.coa_competencias_actividad where coa_codact=@codact and coa_codcom=com_codigo) order by com_descripcion   &lt;/Bound&gt;&lt;/List&gt;" vli_usuario_grabacion="EVA" />
