  <lista vli_id="ConGenContenidoEmpleosActivosDeExpediente" vli_description="Lista los empleados activos de un expediente enviado y que pertenecen a una compañía seleccionada (si se ha seleecionado) con permisos de seg. contenido para consultas genericas" vli_value_source="UseItemData" vli_codfld="int" vli_enable_caching="0" vli_caching_expiration="0" vli_caching_expiration_mins="0" vli_definition="&lt;List&gt;&lt;Bound&gt;&#xA;    select emp_codigo, plz_nombre &#xA;    from exp.emp_empleos&#xA;    join eor.plz_plazas on plz_codigo = emp_codplz&#xA;    join exp.exp_expedientes on exp_codigo = emp_codexp&#xA;   where emp_estado = ''A'' and exp_codigo_alternativo = ''@codigo_alternativo'' and plz_codcia = case when @codcia = 0 then plz_codcia else @codcia end and exists (select null from sco.permiso_empleo_tabla(''$$USER$$'') where emp_codigo = codemp)&#xA;  &lt;/Bound&gt;&lt;/List&gt;" vli_usuario_grabacion="EVA" />
  <lista vli_id="ConGenContenidoExpedientesConEmpleosActivos" vli_description="Lista los expedientes que poseen empleos activos de la compañía seleccionada (si se ha seleccionado) con seguridad de contenido tomando como codigo el codigo alternativo del expediente para consultas genericas" vli_prompt="Empleado" vli_value_source="UseItemData" vli_codfld="string" vli_enable_caching="1" vli_caching_expiration="1" vli_caching_expiration_mins="60" vli_definition="&lt;List&gt;&lt;Bound&gt;&#xA;    with empleos as (&#xA;        select emp_codexp, emp_codigo, plz_nombre&#xA;          from exp.emp_empleos&#xA;          join eor.plz_plazas on plz_codigo = emp_codplz&#xA;         where emp_estado = ''A''&#xA;           and exists (select null from sco.permiso_empleo_tabla(''$$USER$$'') where emp_codigo = codemp)&#xA;           and plz_codcia = case when $$CODCIA$$ = 0 then plz_codcia else $$CODCIA$$ end&#xA;    )&#xA;    select v1.*,&#xA;           plz_nombre NombrePlazaPrimerEmpleo&#xA;      from (&#xA;            select exp_codigo_alternativo, exp_apellidos_nombres, count(*) NumeroEmpleos, min(emp_codigo) CodigoPrimerEmpleo&#xA;              from empleos e1&#xA;              join exp.exp_expedientes on exp_codigo = e1.emp_codexp&#xA;             group by exp_codigo_alternativo, exp_apellidos_nombres&#xA;           ) v1&#xA;      join empleos e2 on e2.emp_codigo = CodigoPrimerEmpleo&#xA;  &lt;/Bound&gt;&lt;/List&gt;" vli_usuario_grabacion="EVA" />
  <lista vli_id="ConGenPortalEmpleosActivosDeExpediente" vli_description="Lista los empleados activos de un expediente enviado y que se tienen permiso de seguridad portal para consultas genericas" vli_value_source="UseItemData" vli_codfld="int" vli_enable_caching="0" vli_caching_expiration="0" vli_caching_expiration_mins="0" vli_definition="&lt;List&gt;&lt;Bound&gt;&#xA;    select emp_codigo, plz_nombre &#xA;    from exp.emp_empleos&#xA;    join eor.plz_plazas on plz_codigo = emp_codplz&#xA;    join exp.exp_expedientes on exp_codigo = emp_codexp&#xA;   where emp_estado = ''A'' and exp_codigo_alternativo = ''@codigo_alternativo''&#xA;         and exists(select null from sco.permiso_emp_x_jerarquia_tabla(''$$USER$$'')&#xA;           where emp_codigo = codemp)&#xA;  &lt;/Bound&gt;&lt;/List&gt;" vli_usuario_grabacion="EVA" />
