  <lista vli_id="DotacionesPorCompania" vli_description="Lista de dotaciones por compania" vli_prompt="Dotación" vli_value_source="UseSqlExpressionInItemData" vli_codfld="int" vli_enable_caching="0" vli_caching_expiration="0" vli_caching_expiration_mins="0" vli_definition="&lt;List&gt;&lt;Bound&gt;&#xA;SELECT dot_codigo, dot_descripcion&#xA;FROM sso.dot_dotaciones&#xA;WHERE dot_codcia = $$CODCIA$$&#xA;  AND exists (select null from sco.permiso_compania_tabla(''$$USER$$'') where dot_codcia = codcia)&#xA;  &lt;/Bound&gt;&lt;/List&gt;" vli_usuario_grabacion="EVA" />
