  <lista vli_id="EdadesComoSegmentacionClima" vli_description="Listado de edades de las encuestas de clima" vli_value_source="UseSqlExpressionInItemData" vli_codfld="string" vli_enable_caching="0" vli_caching_expiration="0" vli_caching_expiration_mins="0" vli_definition="&lt;List&gt;&lt;Unbound&gt;&lt;Item&gt;&lt;ID&gt;select exp_codigo from exp.exp_expedientes where exp_edad &amp;lt; 18&lt;/ID&gt;&lt;Description&gt;Menos de 18&lt;/Description&gt;&lt;/Item&gt;&lt;Item&gt;&lt;ID&gt;select exp_codigo from exp.exp_expedientes where exp_edad between 18 and 25&lt;/ID&gt;&lt;Description&gt;De 18 a 25&lt;/Description&gt;&lt;/Item&gt;&lt;Item&gt;&lt;ID&gt;select exp_codigo from exp.exp_expedientes where exp_edad between 26 and 35&lt;/ID&gt;&lt;Description&gt;De 26 a 35&lt;/Description&gt;&lt;/Item&gt;&lt;Item&gt;&lt;ID&gt;select exp_codigo from exp.exp_expedientes where exp_edad between 36 and 45&lt;/ID&gt;&lt;Description&gt;De 36 a 45&lt;/Description&gt;&lt;/Item&gt;&lt;Item&gt;&lt;ID&gt;select exp_codigo from exp.exp_expedientes where exp_edad between 46 and 55&lt;/ID&gt;&lt;Description&gt;De 46 a 55&lt;/Description&gt;&lt;/Item&gt;&lt;Item&gt;&lt;ID&gt;select exp_codigo from exp.exp_expedientes where exp_edad &amp;gt; 55&lt;/ID&gt;&lt;Description&gt;Más de 55&lt;/Description&gt;&lt;/Item&gt;&lt;/Unbound&gt;&lt;/List&gt;" vli_usuario_grabacion="EVA" />
  <lista vli_id="EdadesEncuestasClima" vli_description="Listado de edades de las encuestas de clima" vli_value_source="UseItemData" vli_codfld="int" vli_enable_caching="0" vli_caching_expiration="0" vli_caching_expiration_mins="0" vli_definition="&lt;List&gt;&lt;Unbound&gt;&lt;Item&gt;&lt;ID&gt;Menos de 18&lt;/ID&gt;&lt;Description&gt;Menos de 18&lt;/Description&gt;&lt;/Item&gt;&lt;Item&gt;&lt;ID&gt;De 18 a 25&lt;/ID&gt;&lt;Description&gt;De 18 a 25&lt;/Description&gt;&lt;/Item&gt;&lt;Item&gt;&lt;ID&gt;De 26 a 35&lt;/ID&gt;&lt;Description&gt;De 26 a 35&lt;/Description&gt;&lt;/Item&gt;&lt;Item&gt;&lt;ID&gt;De 36 a 45&lt;/ID&gt;&lt;Description&gt;De 36 a 45&lt;/Description&gt;&lt;/Item&gt;&lt;Item&gt;&lt;ID&gt;De 46 a 55&lt;/ID&gt;&lt;Description&gt;De 46 a 55&lt;/Description&gt;&lt;/Item&gt;&lt;Item&gt;&lt;ID&gt;Mas de 55&lt;/ID&gt;&lt;Description&gt;Más de 55&lt;/Description&gt;&lt;/Item&gt;&lt;/Unbound&gt;&lt;/List&gt;" vli_usuario_grabacion="EVA" />
  <lista vli_id="ElementosPorCompania" vli_description="Todos los elementos por compañía" vli_prompt="Elemento" vli_value_source="UseSqlExpressionInItemData" vli_codfld="int" vli_enable_caching="0" vli_caching_expiration="0" vli_caching_expiration_mins="0" vli_definition="&lt;List&gt;&lt;Bound&gt;&#xA;SELECT ele_codigo, ele_descripcion&#xA;FROM sso.ele_elementos&#xA;WHERE ele_codcia = $$CODCIA$$&#xA;  AND exists (select null from sco.permiso_compania_tabla(''$$USER$$'') where ele_codcia = codcia)&#xA;  AND ele_codtie = @codtie&#xA;  AND NOT EXISTS (SELECT NULL&#xA;          FROM sso.epr_elementos_procesos&#xA;          WHERE epr_codele = ele_codigo&#xA;            AND epr_codpro = @codpro) &#xA;  &lt;/Bound&gt;&lt;/List&gt;" vli_usuario_grabacion="EVA" />
