  <lista vli_id="EmpleadoEstadosActivos" vli_description="Listado de los posibles estados que puede tener un empleado cuando esta activo" vli_value_source="UseItemData" vli_codfld="string" vli_enable_caching="0" vli_caching_expiration="0" vli_caching_expiration_mins="0" vli_definition="&lt;List&gt;&lt;Unbound&gt;&lt;Item&gt;&lt;ID&gt;A&lt;/ID&gt;&lt;Description&gt;{Activo, Empleado}&lt;/Description&gt;&lt;/Item&gt;&lt;Item&gt;&lt;ID&gt;S&lt;/ID&gt;&lt;Description&gt;{Suspendido, Empleado}&lt;/Description&gt;&lt;/Item&gt;&lt;/Unbound&gt;&lt;/List&gt;" vli_usuario_grabacion="EVA" />
  <lista vli_id="EmpleadosAsignacionJornadaSeguridadContenido" vli_description="Listado para los empleados permitidos en las asignaciones de jornada por seguridad de Contenido (El primer elemento debe de ser el código de empleado)" vli_value_source="UseItemData" vli_codfld="int" vli_enable_caching="0" vli_caching_expiration="0" vli_caching_expiration_mins="0" vli_definition="&lt;List&gt;&lt;Bound&gt;&#xA;    select emp_codigo, (case emp_estado when ''A'' then ''Activo'' when ''R'' then ''Retirado'' when ''N'' then ''Nuevo'' else emp_estado end) + '' - '' + exp_apellidos_nombres + '' ('' + plz_nombre + '')'' emp_nombre_plaza, exp_codigo_alternativo from exp.emp_empleos join exp.exp_expedientes on exp_codigo = emp_codexp join eor.plz_plazas on plz_codigo = emp_codplz where exists (select null from sco.permiso_empleo_tabla(''$$USER$$'') where emp_codigo = codemp) and (isnull(''@texto'', ''null'') = ''null'' or cast(emp_codigo as varchar) = ''@texto'' or exp_codigo_alternativo like ''%@texto%'' or exp_apellidos_nombres like ''%@texto%'' or plz_nombre like ''%@texto%'')&#xA;  &lt;/Bound&gt;&lt;/List&gt;" vli_usuario_grabacion="EVA" />
  <lista vli_id="EmpleadosASustituirDeConcursoYCompaniaEnviado" vli_description="Lista los empleados a sustituir de la compañía y concurso enviado" vli_value_source="UseItemData" vli_codfld="string" vli_enable_caching="0" vli_caching_expiration="0" vli_caching_expiration_mins="0" vli_definition="&lt;List&gt;&lt;Bound&gt;&#xA;    select emp_codigo, exp_apellidos_nombres + '' ('' + plz_nombre + '')''&#xA;    from rys.sec_sust_emp_concurso&#xA;      join exp.emp_empleos on emp_codigo = sec_codemp&#xA;      join exp.exp_expedientes on exp_codigo = emp_codexp&#xA;      join eor.plz_plazas on plz_codigo = emp_codplz&#xA;     where plz_codcia = SUBSTRING(''@codcos_codcia'', CHARINDEX(''|'', ''@codcos_codcia'', 0) + 1, LEN(''@codcos_codcia'') - CHARINDEX(''|'', ''@codcos_codcia'', 0)) and &#xA;           sec_codcos = SUBSTRING(''@codcos_codcia'', 1, CHARINDEX(''|'', ''@codcos_codcia'', 0) - 1)&#xA;  &lt;/Bound&gt;&lt;/List&gt;" vli_usuario_grabacion="EVA" />
