  <lista vli_id="EmpleadosEvaluadoresDeCompaniaEnviada" vli_description="Lista los empleados evaluadores de la compañia enviada para el estudio socio-economico de los candidatos" vli_value_source="UseItemData" vli_codfld="string" vli_enable_caching="0" vli_caching_expiration="0" vli_caching_expiration_mins="0" vli_definition="&lt;List&gt;&lt;Bound&gt;&#xA;  select exp_codigo_alternativo, &#xA;         exp_apellidos_nombres &#xA;    from exp.exp_expedientes v1&#xA;   where exists(select 1 &#xA;                  from exp.eme_emp_evaluadores_v v2&#xA;                 where ccp_codigo &amp;gt;= 1&#xA;                   and ccp_codigo &amp;lt;= 3&#xA;                   and cia_codigo = @codcia&#xA;                   and v2.exp_codigo = v1.exp_codigo)&#xA;order by exp_apellidos_nombres&#xA;  &lt;/Bound&gt;&lt;/List&gt;" vli_usuario_grabacion="EVA" />
  <lista vli_id="EmpleadosFirmantesContrato" vli_description="Empleados que tienen marcado firma contrato" vli_value_source="UseSqlExpressionInItemData" vli_codfld="string" vli_enable_caching="0" vli_caching_expiration="0" vli_caching_expiration_mins="0" vli_definition="&lt;List&gt;&lt;Bound&gt;&#xA;   SELECT exp_codigo, exp_nombres_apellidos&#xA;FROM exp.emp_empleos&#xA;JOIN exp.exp_expedientes ON exp_expedientes.exp_codigo = emp_empleos.emp_codexp &#xA;WHERE isnull(gen.get_pb_field_data(emp_property_bag_data,''emp_firmante''), ''N'') = ''S''&#xA;AND emp_estado = ''A''&#xA;  &lt;/Bound&gt;&lt;/List&gt;" vli_usuario_grabacion="EVA" />
  <lista vli_id="EmpleadosRetiradosSinLiquidacion" vli_description="Lista los empleados en estado retirado que tienen un retiro y no poseen la liquidación" vli_prompt="Seleccionar" vli_value_source="UseItemData" vli_codfld="string" vli_enable_caching="0" vli_caching_expiration="0" vli_caching_expiration_mins="0" vli_definition="&lt;List&gt;&lt;Bound&gt;&#xA;    select exp_codigo_alternativo, exp_nombres_apellidos from exp.exp_expedientes join exp.emp_empleos on emp_codexp = exp_codigo join eor.plz_plazas on plz_codigo = emp_codplz join acc.ret_retiros on ret_codemp = emp_codigo where ((ret_estado = ''Autorizado'' and emp_estado = ''R'') or cfg.get_parametro_bit(''LiquidacionUtilizaRetirosNoAutorizados'') = 1) and not exists (select 1 from acc.lie_liquidaciones where lie_codret = ret_codigo and lie_codigo != @codlie and lie_estado != ''Denegado'') and plz_codcia = $$CODCIA$$ and exists (select null from sco.permiso_empleo_tabla(''$$USER$$'') where emp_codigo = codemp)&#xA;  &lt;/Bound&gt;&lt;/List&gt;" vli_usuario_grabacion="EVA" />
  <lista vli_id="EmpleosActDeEmpleoEnviado" vli_description="Lista los empleos activos del grupo corporativo al que pertenece el empleo enviado" vli_value_source="UseItemData" vli_codfld="int" vli_enable_caching="0" vli_caching_expiration="0" vli_caching_expiration_mins="0" vli_definition="&lt;List&gt;&lt;Bound&gt;&#xA;select exp_codigo_alternativo,&#xA;       exp_apellidos_nombres &#xA;  from exp.exp_expedientes&#xA; where exists(&#xA;        select null &#xA;          from exp.emp_empleos&#xA;          join eor.plz_plazas on plz_codigo = emp_codplz&#xA;          join eor.cia_companias on cia_codigo = plz_codcia&#xA;         where emp_codexp = exp_codigo&#xA;           and cia_codgrc in (&#xA;                select cia_codgrc &#xA;                  from exp.emp_empleos e1 &#xA;                  join eor.plz_plazas p1 on p1.plz_codigo = e1.emp_codplz &#xA;                  join eor.cia_companias c1 on c1.cia_codigo = p1.plz_codcia &#xA;                 where e1.emp_codigo = @codemp)&#xA;           and emp_estado = ''A''&#xA;           and exists (select null from sco.permiso_empleo_tabla(''$$USER$$'') where emp_codigo = codemp))&#xA;&lt;/Bound&gt;&lt;/List&gt;" vli_usuario_grabacion="EVA" />
  <lista vli_id="EmpleosActivosAutDeCompaniaSeleccionada" vli_description="Lista los empleos activos autorizados de la compañía seleccionada, con el nombre del empleado y su plaza como texto" vli_value_source="UseItemData" vli_codfld="int" vli_enable_caching="0" vli_caching_expiration="0" vli_caching_expiration_mins="0" vli_definition="&lt;List&gt;&lt;Bound&gt;&#xA;    select emp_codigo, exp_apellidos_nombres  + '' ('' + plz_nombre + '')'' emp_nombre_plaza&#xA;  from exp.emp_empleos &#xA;  join exp.exp_expedientes on exp_codigo = emp_codexp &#xA;  join eor.plz_plazas on plz_codigo = emp_codplz&#xA; where emp_estado = ''A'' and plz_codcia = $$CODCIA$$ and exists (select null from sco.permiso_empleo_tabla(''$$USER$$'') where emp_codigo = codemp)&#xA; order by exp_apellidos_nombres&#xA;  &lt;/Bound&gt;&lt;/List&gt;" vli_usuario_grabacion="EVA" />
