  <lista vli_id="EmpleosActivosCodAlternativoFiltradosPorCiaCdtUni" vli_description="Lista los empleos activos del código alternativo enviado filtrados por el código de compañía, centros de trabajo y unidad" vli_value_source="UseItemData" vli_codfld="int" vli_enable_caching="0" vli_caching_expiration="0" vli_caching_expiration_mins="0" vli_definition="&lt;List&gt;&lt;Bound&gt;&#xA;    select emp_codigo, plz_nombre&#xA;      from exp.emp_empleos&#xA;      join exp.exp_expedientes on exp_codigo = emp_codexp&#xA;      join eor.plz_plazas on plz_codigo = emp_codplz&#xA;      join eor.cia_companias on cia_codigo = plz_codcia&#xA;      join eor.cdt_centros_de_trabajo on cdt_codigo = plz_codcdt&#xA;      join eor.uni_unidades on uni_codigo = plz_coduni&#xA;     where plz_codcia = @codcia &#xA;       and (isnull(@codcdt, ''-1'') = ''-1'' or plz_codcdt = @codcdt) &#xA;       and (isnull(@coduni, ''-1'') = ''-1'' or plz_coduni = @coduni)&#xA;       and emp_estado = ''A''&#xA;     and exp_codigo_alternativo = ''@codigo_alternativo''&#xA;  &lt;/Bound&gt;&lt;/List&gt;" vli_usuario_grabacion="EVA" />
  <lista vli_id="EmpleosActivosDeCodigoAlternativoEnviado" vli_description="Lista los empleos activos para un expediente de codigo alternativo enviado" vli_value_source="UseItemData" vli_codfld="int" vli_enable_caching="0" vli_caching_expiration="0" vli_caching_expiration_mins="0" vli_definition="&lt;List&gt;&lt;Bound&gt;&#xA;    select emp_codigo, plz_nombre&#xA;  from exp.emp_empleos&#xA;  join eor.plz_plazas on plz_codigo = emp_codplz&#xA;  join exp.exp_expedientes on exp_codigo = emp_codexp&#xA; where emp_estado = ''A''&#xA;   and exp_codigo_alternativo = ''@codigo_alternativo''&#xA;  &lt;/Bound&gt;&lt;/List&gt;" vli_usuario_grabacion="EVA" />
  <lista vli_id="EmpleosActivosDeCodigoAlternativoEnviadoPorGrupo" vli_description="Lista los empleos activos que pertenecen al grupo seleccionado de un codigo alternativo enviado" vli_prompt="Empleado" vli_value_source="UseItemData" vli_codfld="int" vli_enable_caching="0" vli_caching_expiration="0" vli_caching_expiration_mins="0" vli_definition="&lt;List&gt;&lt;Bound&gt;&#xA;    select emp_codigo, plz_nombre&#xA;    from exp.emp_empleos &#xA;    join eor.plz_plazas on plz_codigo = emp_codplz&#xA;    join eor.cia_companias on cia_codigo = plz_codcia&#xA;    join exp.exp_expedientes on exp_codigo = emp_codexp&#xA;     where emp_estado = ''A''&#xA;     and exp_codigo_alternativo = ''@codigo_alternativo''&#xA;     and exists (select null from sco.permiso_empleo_tabla(''$$USER$$'') where emp_codigo = codemp)&#xA;     and cia_codgrc = $$CODGRC$$&#xA;  &lt;/Bound&gt;&lt;/List&gt;" vli_usuario_grabacion="EVA" />
  <lista vli_id="EmpleosActivosDeExpedienteAlternativoEnviado" vli_description="Lista los empleados activos de un expediente enviado y verificando seguridad por compañía" vli_value_source="UseItemData" vli_codfld="int" vli_enable_caching="0" vli_caching_expiration="0" vli_caching_expiration_mins="0" vli_definition="&lt;List&gt;&lt;Bound&gt;&#xA;select emp_codigo, plz_nombre &#xA;  from exp.emp_empleos &#xA;  join eor.plz_plazas on plz_codigo = emp_codplz &#xA;  join exp.exp_expedientes on exp_codigo = emp_codexp &#xA; where emp_estado = ''A'' and exp_codigo_alternativo = ''@codigo_alternativo'' and plz_codcia = $$CODCIA$$ and exists (select null from sco.permiso_empleo_tabla(''$$USER$$'') where emp_codigo = codemp)&#xA;&lt;/Bound&gt;&lt;/List&gt;" vli_usuario_grabacion="EVA" />
