  <lista vli_id="EtapasEvaluacionConcursoDeGrupoEnviado" vli_description="Lista las etapas de evaluación en el concurso de seleccion yq ue son aplicables al grupo corporativo enviado" vli_value_source="UseItemData" vli_codfld="int" vli_enable_caching="0" vli_caching_expiration="0" vli_caching_expiration_mins="0" vli_definition="&lt;List&gt;&lt;Bound&gt;&#xA;    select fec_codigo, fec_nombre from rys.fec_fases_evaluaciones where fec_codgrc is null or fec_codgrc = @codgrc&#xA;  &lt;/Bound&gt;&lt;/List&gt;" vli_usuario_grabacion="EVA" />
  <lista vli_id="EventosDeGrupoEnviado" vli_description="Lista los eventos del grupo enviado" vli_value_source="UseItemData" vli_codfld="int" vli_enable_caching="0" vli_caching_expiration="0" vli_caching_expiration_mins="0" vli_definition="&lt;List&gt;&lt;Bound&gt;&#xA;  select eve_codigo, eve_nombre from cap.eve_eventos join cap.are_areas_cursos on are_codigo = eve_codare where are_codgrc = @codgrc&#xA;  &lt;/Bound&gt;&lt;/List&gt;" vli_usuario_grabacion="EVA" />
  <lista vli_id="EventosParaSolicitudDeGrupoEnviado" vli_description="Lista los eventos que estan programado y EnEjecucion del grupo corporativo enviado" vli_value_source="UseItemData" vli_codfld="int" vli_enable_caching="0" vli_caching_expiration="0" vli_caching_expiration_mins="0" vli_definition="&lt;List&gt;&lt;Bound&gt;&#xA;  SELECT eve_codigo, eve_nombre&#xA;    from cap.eve_eventos&#xA;    join cap.are_areas_cursos on are_codigo = eve_codare&#xA;  where eve_estado = ''Programado'' AND are_codgrc = @codgrc&#xA;  &lt;/Bound&gt;&lt;/List&gt;" vli_usuario_grabacion="EVA" />
  <lista vli_id="ExpConEmpleosSolicitantesDelGrupoDeEmpleoEnviado" vli_description="Muestra los expedientes con empleos solicitantes del grupo corporativo del empleo enviado" vli_value_source="UseItemData" vli_codfld="string" vli_enable_caching="0" vli_caching_expiration="0" vli_caching_expiration_mins="0" vli_definition="&lt;List&gt;&lt;Bound&gt;&#xA;      select distinct exp_codigo_alternativo, exp_apellidos_nombres &#xA;        from exp.eso_emp_solicitantes &#xA;       where grc_codigo = (select cia_codgrc from exp.emp_empleos join eor.plz_plazas on plz_codigo = emp_codplz join eor.cia_companias on cia_codigo = plz_codcia where emp_codigo = @codemp)  &#xA;    order by exp_apellidos_nombres&#xA;  &lt;/Bound&gt;&lt;/List&gt;" vli_usuario_grabacion="EVA" />
  <lista vli_id="ExpConEmpleoTempRangoFechaDeCompaniaEnviada" vli_description="Muestra los expedientes que poseen empleos temporales en rango de fechas de la compañía enviada" vli_value_source="UseItemData" vli_codfld="string" vli_enable_caching="1" vli_caching_expiration="1" vli_caching_expiration_mins="480" vli_definition="&lt;List&gt;&lt;Bound&gt;&#xA;  select exp_codigo_alternativo, exp_apellidos_nombres from exp.exp_expedientes&#xA;   where exists(select null from exp.emp_empleos&#xA;                  join exp.tco_tipos_de_contrato on tco_codigo = emp_codtco&#xA;                  join eor.plz_plazas on plz_codigo = emp_codplz&#xA;                 where emp_codexp = exp_codigo and tco_es_temporal = 1&#xA;                   and plz_codcia = @codcia&#xA;                   and emp_estado = ''A''&#xA;                   and convert(datetime, convert(varchar, emp_contrato_fin, 101), 101) between convert(datetime, convert(varchar, getdate() - @dias_atras, 101), 101) and convert(datetime, convert(varchar, getdate() + @dias_adelante, 101), 101))&#xA;  &lt;/Bound&gt;&lt;/List&gt;" vli_usuario_grabacion="EVA" />
  <lista vli_id="ExpConEmpRetiradosAutorizadosDeCiaSeleccionada" vli_description="Listado de expedientes con empleos retirados que están autorizados para la compañía enviada" vli_value_source="UseItemData" vli_codfld="string" vli_enable_caching="0" vli_caching_expiration="0" vli_caching_expiration_mins="0" vli_definition="&lt;List&gt;&lt;Bound&gt;&#xA;select distinct exp_codigo_alternativo, exp_nombres_apellidos&#xA;  from exp.exp_expedientes&#xA;  join exp.emp_empleos on emp_codexp = exp_codigo&#xA;  join eor.plz_plazas on plz_codigo = emp_codplz&#xA;  join acc.ret_retiros on ret_codemp = emp_codigo&#xA; where ret_estado = ''Autorizado'' and emp_estado = ''R'' &#xA;   and not exists (select 1 from acc.rre_reversiones_retiro where rre_codret = ret_codigo and rre_estado != ''Denegado'')&#xA;   and plz_codcia = $$CODCIA$$&#xA;   and exists (select null from sco.permiso_empleo_tabla(''$$USER$$'') where emp_codigo = codemp)&#xA;   and convert(date, ret_fecha_retiro) &amp;gt;= (convert(date, (getdate() - cfg.get_parametro_int(''NumeroDiasAnterioresALaActualParaRevertirRetiros''))))&#xA; order by exp_nombres_apellidos&#xA;&lt;/Bound&gt;&lt;/List&gt;" vli_usuario_grabacion="EVA" />
  <lista vli_id="ExpDeEmpleosConContratacionesAutDePortal" vli_description="Listado de los expedientes de empleos que se generaron a partir de la acción de contratación a los que el usuario autenticado tiene permiso" vli_value_source="UseItemData" vli_codfld="string" vli_enable_caching="0" vli_caching_expiration="0" vli_caching_expiration_mins="0" vli_definition="&lt;List&gt;&lt;Bound&gt;&#xA;select distinct exp_codigo_alternativo, exp_nombres_apellidos&#xA;  from exp.exp_expedientes&#xA;  join exp.emp_empleos on emp_codexp = exp_codigo&#xA;  join eor.plz_plazas on plz_codigo = emp_codplz&#xA;  join acc.con_contrataciones on con_codemp_generado = emp_codigo&#xA; where con_estado = ''Autorizado'' and con_codemp_generado is not null&#xA;   and not exists (select 1 from acc.rco_reversiones_contratos where rco_codcon = con_codigo and rco_estado != ''Denegado'')&#xA;   and exists(select null from sco.permiso_emp_x_jerarquia_tabla(''$$USER$$'') where con_codemp_generado = codemp)&#xA;   and convert(date, con_contrato_ini) &amp;gt;= (convert(date, (getdate() - cfg.get_parametro_int(''NumeroDiasAnterioresALaActualParaRevertirContrataciones''))))&#xA; order by exp_nombres_apellidos&#xA;&lt;/Bound&gt;&lt;/List&gt;" vli_usuario_grabacion="EVA" />
  <lista vli_id="ExpDeEmpleosConContratacionesDeCiaSeleccionada" vli_description="Listado de los expedientes de empleos que se generaron a partir de la acción de contratación" vli_value_source="UseItemData" vli_codfld="string" vli_enable_caching="0" vli_caching_expiration="0" vli_caching_expiration_mins="0" vli_definition="&lt;List&gt;&lt;Bound&gt;&#xA;select distinct exp_codigo_alternativo, exp_nombres_apellidos&#xA;  from exp.exp_expedientes&#xA;  join exp.emp_empleos on emp_codexp = exp_codigo&#xA;  join eor.plz_plazas on plz_codigo = emp_codplz&#xA;  join acc.con_contrataciones on con_codemp_generado = emp_codigo&#xA; where con_estado = ''Autorizado'' and con_codemp_generado is not null&#xA;   and not exists (select 1 from acc.rco_reversiones_contratos where rco_codcon = con_codigo and rco_estado != ''Denegado'')&#xA;   and plz_codcia = $$CODCIA$$&#xA;   and exists (select null from sco.permiso_plaza_tabla(''$$USER$$'') where plz_codigo = codplz)&#xA;   and convert(date, con_contrato_ini) &amp;gt;= (convert(date, (getdate() - cfg.get_parametro_int(''NumeroDiasAnterioresALaActualParaRevertirContrataciones''))))&#xA; order by exp_nombres_apellidos&#xA;&lt;/Bound&gt;&lt;/List&gt;" vli_usuario_grabacion="EVA" />
  <lista vli_id="ExpedienteConEmpleosActivosDeReclutamientoAut" vli_description="Lista los expedientes con empleos activos de reclutamiento autorizados" vli_value_source="UseItemData" vli_codfld="string" vli_enable_caching="0" vli_caching_expiration="0" vli_caching_expiration_mins="0" vli_definition="&lt;List&gt;&lt;Bound&gt;&#xA;    select exp_codigo_alternativo, exp_apellidos_nombres &#xA;      from exp.exp_expedientes&#xA;     where exists(select 1 &#xA;                    from exp.emr_emp_reclutamiento_v &#xA;                   where emp_estado = ''A''&#xA;                     and exists (select null from sco.permiso_empleo_tabla(''$$USER$$'') where emp_codigo = codemp)&#xA;                     and emp_codexp = exp_codigo&#xA;           and cia_codgrc = $$CODGRC$$)&#xA;  &lt;/Bound&gt;&lt;/List&gt;" vli_usuario_grabacion="EVA" />
  <lista vli_id="ExpedienteEvaluadoresDeEmpleoEvaluado" vli_description="Lista los expedientes con empleos activos cuya plaza tiene asignaciones como evaluador para un período y empleado a evaluar enviado" vli_value_source="UseItemData" vli_codfld="string" vli_enable_caching="0" vli_caching_expiration="0" vli_caching_expiration_mins="0" vli_definition="&lt;List&gt;&lt;Bound&gt;&#xA;  select exp_codigo_alternativo, exp_apellidos_nombres&#xA;      from exp.exp_expedientes&#xA;     where exists(&#xA;           select null from eva.ase_asigna_evaluadores&#xA;               join eor.plz_plazas on plz_codigo = ase_codplz_evaluador&#xA;               join exp.emp_empleos on emp_codplz = plz_codigo&#xA;              where emp_codexp = exp_codigo&#xA;          and emp_estado = ''A''&#xA;                and ase_codpev = @codpev&#xA;                and ase_codplz = (select emp_codplz from exp.emp_empleos e where e.emp_codigo = @codemp_evaluado))&#xA;  &lt;/Bound&gt;&lt;/List&gt;" vli_usuario_grabacion="EVA" />
  <lista vli_id="ExpedienteEvaluadosConAsignacionDePeriodo" vli_description="Lista los expedientes de los empleos activos cuya plaza tiene asignaciones como evaluado para un período enviado" vli_value_source="UseItemData" vli_codfld="string" vli_enable_caching="0" vli_caching_expiration="0" vli_caching_expiration_mins="0" vli_definition="&lt;List&gt;&lt;Bound&gt;&#xA;  select exp_codigo_alternativo, exp_apellidos_nombres&#xA;      from exp.exp_expedientes&#xA;     where exists(&#xA;           select null from eva.ase_asigna_evaluadores&#xA;               join eor.plz_plazas on plz_codigo = ase_codplz&#xA;               join exp.emp_empleos on emp_codplz = plz_codigo&#xA;              where emp_codexp = exp_codigo&#xA;          and emp_estado = ''A''&#xA;                and ase_codpev = @codpev)&#xA;  &lt;/Bound&gt;&lt;/List&gt;" vli_usuario_grabacion="EVA" />
  <lista vli_id="ExpedientesActivosJefesDeCodigoEmpleoEnviado" vli_description="Lista de Codigo alternativo de los jefes para un empleo" vli_value_source="UseItemData" vli_codfld="string" vli_enable_caching="0" vli_caching_expiration="0" vli_caching_expiration_mins="0" vli_definition="&lt;List&gt;&lt;Bound&gt;&#xA;select &#xA;  exp_codigo_alternativo, exp_nombres_apellidos + case when pjf_tipo_relacion = ''A'' then ''({Administrativa,DependenciaPlaza})'' else ''({Funcional,DependenciaPlaza})'' end  exp_nombres_apellidos&#xA;from &#xA;  exp.emp_empleos empsub&#xA;  join eor.pjf_plaza_jefes on pjf_codplz = empsub.emp_codplz&#xA;  join exp.emp_empleos empjef on empjef.emp_codplz = pjf_codplz_jefe&#xA;  join exp.exp_expedientes on exp_codigo = empjef.emp_codexp&#xA;where &#xA;  empsub.emp_estado = ''A''&#xA;  and empsub.emp_codigo = @codigo_alternativo&#xA;  &lt;/Bound&gt;&lt;/List&gt;" vli_usuario_grabacion="EVA" />
  <lista vli_id="ExpedientesConEmpleosActDeEmpleoEnviado" vli_description="Lista los empleos activos del grupo corporativo al que pertenece el empleo enviado" vli_value_source="UseItemData" vli_codfld="int" vli_enable_caching="0" vli_caching_expiration="0" vli_caching_expiration_mins="0" vli_definition="&lt;List&gt;&lt;Bound&gt;&#xA;select emp_codigo, plz_nombre &#xA;  from exp.emp_empleos&#xA;  join exp.exp_expedientes on exp_codigo = emp_codexp&#xA;  join eor.plz_plazas on plz_codigo = emp_codplz&#xA;  join eor.cia_companias on cia_codigo = plz_codcia&#xA; where exp_codigo_alternativo = ''@codigo_alternativo''&#xA;   and emp_estado = ''A''&#xA;   and exists (select null from sco.permiso_empleo_tabla(''$$USER$$'') where emp_codigo = codemp)&#xA;   and cia_codgrc in (&#xA;        select cia_codgrc &#xA;          from exp.emp_empleos e1 &#xA;          join eor.plz_plazas p1 on p1.plz_codigo = e1.emp_codplz &#xA;          join eor.cia_companias c1 on c1.cia_codigo = p1.plz_codcia &#xA;         where e1.emp_codigo = @codemp)&#xA;  &lt;/Bound&gt;&lt;/List&gt;" vli_usuario_grabacion="EVA" />
  <lista vli_id="ExpedientesConEmpleosActivos" vli_description="Lista los expedientes que poseen al menos un empleo activo" vli_value_source="UseItemData" vli_codfld="string" vli_enable_caching="0" vli_caching_expiration="0" vli_caching_expiration_mins="0" vli_definition="&lt;List&gt;&lt;Bound&gt;&#xA;    select exp_codigo_alternativo, exp_apellidos_nombres&#xA;  from exp.exp_expedientes&#xA; where exists(select 1 from exp.emp_empleos where emp_estado = ''A'' and emp_codexp = exp_codigo)&#xA;  &lt;/Bound&gt;&lt;/List&gt;" vli_usuario_grabacion="EVA" />
  <lista vli_id="ExpedientesConEmpleosActivosDeCompania" vli_description="Lista los expedientes que poseen empleos activos de la compañía seleccionada con seguridad de contenido tomando como codigo el codigo alternativo del expediente" vli_prompt="Empleado" vli_value_source="UseItemData" vli_codfld="string" vli_enable_caching="1" vli_caching_expiration="1" vli_caching_expiration_mins="60" vli_definition="&lt;List&gt;&lt;Bound&gt;&#xA;    with empleos as (&#xA;        select emp_codexp, emp_codigo, plz_nombre&#xA;          from exp.emp_empleos&#xA;          join eor.plz_plazas on plz_codigo = emp_codplz&#xA;          join eor.cia_companias on cia_codigo = plz_codcia&#xA;         where emp_estado = ''A''&#xA;           and exists (select null from sco.permiso_empleo_tabla(''$$USER$$'') where emp_codigo = codemp)&#xA;           and plz_codcia = $$CODCIA$$&#xA;    )&#xA;    select v1.*,&#xA;           plz_nombre NombrePlazaPrimerEmpleo&#xA;      from (&#xA;            select exp_codigo_alternativo, exp_apellidos_nombres + (case isnull(ide_numero, '''') when '''' then '''' else '' ('' + coalesce(ide_numero, '''') + '')'' end) as exp_apellidos_nombres, count(*) NumeroEmpleos, min(emp_codigo) CodigoPrimerEmpleo&#xA;              from empleos e1&#xA;              join exp.exp_expedientes on exp_codigo = e1.emp_codexp&#xA;&#x9;&#x9;&#x9;  left join (select ide_codexp, ide_numero from exp.ide_documentos_identificacion join exp.tdo_tipos_de_documento on tdo_codigo = ide_codtdo&#xA;&#x9;&#x9;&#x9;&#x9;&#x9;&#x9; where ide_codtdo = gen.get_valor_parametro_int(''CodigoDoc_Cedula'', tdo_codpai, null, null, null)) ide&#xA;&#x9;&#x9;&#x9;  on ide_codexp = emp_codexp&#xA;             group by exp_codigo_alternativo, exp_apellidos_nombres, ide_numero&#xA;           ) v1&#xA;      join empleos e2 on e2.emp_codigo = CodigoPrimerEmpleo&#xA;  &lt;/Bound&gt;&lt;/List&gt;" vli_usuario_grabacion="EVA" />
  <lista vli_id="ExpedientesConEmpleosActivosDeGrupo" vli_description="Lista los expedientes que poseen empleos activos del grupo corporativo seleccionado" vli_prompt="Empleado" vli_value_source="UseItemData" vli_codfld="string" vli_enable_caching="0" vli_caching_expiration="0" vli_caching_expiration_mins="0" vli_definition="&lt;List&gt;&lt;Bound&gt;&#xA;    select exp_codigo_alternativo, exp_apellidos_nombres&#xA;      from exp.exp_expedientes&#xA;     where exists(select 1 &#xA;                    from exp.emp_empleos &#xA;                    join eor.plz_plazas on plz_codigo = emp_codplz&#xA;                    join eor.cia_companias on cia_codigo = plz_codcia&#xA;                   where emp_codexp = exp_codigo and emp_estado = ''A''&#xA;                     and exists (select null from sco.permiso_empleo_tabla(''$$USER$$'') where emp_codigo = codemp)&#xA;                     and cia_codgrc = $$CODGRC$$)&#xA;  &lt;/Bound&gt;&lt;/List&gt;" vli_usuario_grabacion="EVA" />
  <lista vli_id="ExpedientesConEmpleosActivosParaAuditoria" vli_description="Lista los expedientes que poseen empleos activos de la compañía seleccionada con seguridad de contenido tomando como codigo el codigo alternativo del expediente" vli_prompt="Empleado" vli_value_source="UseItemData" vli_codfld="string" vli_enable_caching="0" vli_caching_expiration="0" vli_caching_expiration_mins="0" vli_definition="&lt;List&gt;&lt;Bound&gt;&#xA;SELECT exp_codigo_alternativo, exp_apellidos_nombres&#xA;FROM exp.exp_expedientes&#xA;  JOIN exp.emp_empleos ON exp_codigo = emp_codexp&#xA;  JOIN eor.plz_plazas ON plz_codigo = emp_codplz&#xA;WHERE emp_estado = ''A''&#xA;  AND exists (select null from sco.permiso_empleo_tabla(''$$USER$$'') where emp_codigo = codemp)&#xA;  AND plz_codcia = $$CODCIA$$&#xA;  AND NOT EXISTS (SELECT NULL&#xA;          FROM sso.aau_auditores_auditorias&#xA;          WHERE emp_codigo = aau_codemp&#xA;            AND aau_codaud = @codaud) &#xA;  &lt;/Bound&gt;&lt;/List&gt;" vli_usuario_grabacion="EVA" />
  <lista vli_id="ExpedientesConEmpleosActivosProcesaAsistencia" vli_description="Filtro de expedientes con empleos activos para filtrar el procesamiento de asistencia" vli_prompt="Código Alternativo" vli_value_source="UseSqlExpressionInItemData" vli_codfld="string" vli_enable_caching="0" vli_caching_expiration="0" vli_caching_expiration_mins="0" vli_definition="&lt;List&gt;&lt;Bound&gt;&#xA;select distinct exp_codigo_alternativo, exp_nombres_apellidos&#xA;  from exp.emp_empleos&#xA;  join exp.exp_expedientes on exp_codigo = emp_codexp&#xA;  join eor.plz_plazas on plz_codigo = emp_codplz&#xA;  join eor.cia_companias on cia_codigo = plz_codcia&#xA;where plz_codcia = $$CODCIA$$&#xA;and (exists(select null from sco.permiso_emp_x_jerarquia_tabla(''$$USER$$'') v where emp_codigo = codemp)  &#xA;     or exists (select null from sco.permiso_empleo_tabla(''$$USER$$'') where emp_codigo = codemp))&#xA;&lt;/Bound&gt;&lt;/List&gt;" vli_usuario_grabacion="EVA" />
  <lista vli_id="ExpedientesConEmpleosActivosReportes" vli_description="Filtro de expedientes con empleos activos para filtos de reportes" vli_prompt="Código Empleado" vli_value_source="UseSqlExpressionInItemData" vli_codfld="int" vli_enable_caching="0" vli_caching_expiration="0" vli_caching_expiration_mins="0" vli_definition="&lt;List&gt;&lt;Bound&gt;&#xA;select exp_codigo, exp_nombres_apellidos&#xA;  from exp.emp_empleos&#xA;  join exp.exp_expedientes on exp_codigo = emp_codexp&#xA;  join eor.plz_plazas on plz_codigo = emp_codplz&#xA;  join eor.cia_companias on cia_codigo = plz_codcia&#xA;where plz_codcia = $$CODCIA$$&#xA;and (exists(select null from sco.permiso_emp_x_jerarquia_tabla(''$$USER$$'') v where emp_codigo = codemp)  &#xA;     or exists (select null from sco.permiso_empleo_tabla(''$$USER$$'') where emp_codigo = codemp)&#xA;     or exists (select 1&#xA;                from eva.eva_evaluaciones&#xA;                where EXISTS (select 1&#xA;                        from sec.usr_users&#xA;                        join sec.eus_expediente_usuario on usr_codigo = eus_codusr&#xA;                        join exp.emp_empleos on emp_codexp = eus_codexp&#xA;                        where usr_username = ''$$USER$$''&#xA;                          and emp_codigo = eva_codemp_evaluador)&#xA;                  and emp_codigo = eva_codemp_evaluador))&#xA;&#xA;&lt;/Bound&gt;&lt;/List&gt;" vli_usuario_grabacion="EVA" />
  <lista vli_id="ExpedientesConEmpleosAutDePortal" vli_description="Lista los expedientes que poseen empleos puede ver el usuario autenticado según sus preferencias para el portal" vli_prompt="Expediente" vli_value_source="UseItemData" vli_codfld="string" vli_enable_caching="1" vli_caching_expiration="2" vli_caching_expiration_mins="1" vli_definition="&lt;List&gt;&lt;Bound&gt;&#xA;  select distinct exp_codigo_alternativo, exp_apellidos_nombres&#xA;      from exp.emp_empleos&#xA;    join exp.exp_expedientes on emp_codexp = exp_codigo&#xA;     where exists(select null from sco.permiso_emp_x_jerarquia_tabla(''$$USER$$'')&#xA;           where emp_codigo = codemp)&#xA;       and emp_estado = ''A''&#xA;           order by exp_apellidos_nombres&#xA;  &lt;/Bound&gt;&lt;/List&gt;" vli_usuario_grabacion="EVA" />
  <lista vli_id="ExpedientesConEmpleosAutDePortalSolicitudCap" vli_description="Lista los expedientes que poseen empleos puede ver el usuario autenticado según sus preferencias para el portal en solicitudes de capacitación" vli_prompt="Expediente" vli_value_source="UseItemData" vli_codfld="string" vli_enable_caching="1" vli_caching_expiration="2" vli_caching_expiration_mins="1" vli_definition="&lt;List&gt;&lt;Bound&gt;&#xA;    select distinct exp_codigo_alternativo, exp_apellidos_nombres&#xA;      from exp.emp_empleos&#xA;    join exp.exp_expedientes on exp_codigo = emp_codexp&#xA;    join eor.plz_plazas on plz_codigo = emp_codplz&#xA;    join eor.cia_companias on cia_codigo = plz_codcia&#xA;     where exists(select 1&#xA;          from sco.permiso_emp_x_jerarquia_tabla(''$$USER$$'')&#xA;           where emp_codigo = codemp)&#xA;       and cia_codgrc = @codgrc and emp_estado = ''A''&#xA;    order by exp_apellidos_nombres&#xA;  &lt;/Bound&gt;&lt;/List&gt;" vli_usuario_grabacion="EVA" />
  <lista vli_id="ExpedientesConEmpleosEvaluadoresActDeGrupoEnviado" vli_description="Lista los expediente con empleos activos que tienen asignaciones como evaluadores del grupo corporativo enviado" vli_value_source="UseItemData" vli_codfld="int" vli_enable_caching="0" vli_caching_expiration="0" vli_caching_expiration_mins="0" vli_definition="&lt;List&gt;&lt;Bound&gt;&#xA;  select exp_codigo_alternativo,&#xA;           exp_apellidos_nombres &#xA;      from exp.exp_expedientes&#xA;     where exists(select null &#xA;                    from exp.emp_empleos&#xA;          join eor.plz_plazas on plz_codigo = emp_codplz&#xA;          join eor.cia_companias on cia_codigo = plz_codcia&#xA;                    join eva.ase_asigna_evaluadores on ase_codplz_evaluador = emp_codplz&#xA;                   where emp_codexp = exp_codigo&#xA;             and ase_codpev = @codpev&#xA;             and cia_codgrc = @codgrc&#xA;                     and emp_estado = ''A''&#xA;           and exists (select null from sco.permiso_empleo_tabla(''$$USER$$'') where emp_codigo = codemp))&#xA;  &lt;/Bound&gt;&lt;/List&gt;" vli_usuario_grabacion="EVA" />
  <lista vli_id="ExpedientesConEmpleoSolicitanteDeGrupoEnviado" vli_description="Muestra los expedientes con empleos solicitantes del grupo corporativo enviado" vli_value_source="UseItemData" vli_codfld="string" vli_enable_caching="0" vli_caching_expiration="0" vli_caching_expiration_mins="0" vli_definition="&lt;List&gt;&lt;Bound&gt;&#xA;      select distinct exp_codigo_alternativo, exp_apellidos_nombres from exp.eso_emp_solicitantes where grc_codigo = @codgrc order by exp_apellidos_nombres&#xA;  &lt;/Bound&gt;&lt;/List&gt;" vli_usuario_grabacion="EVA" />
  <lista vli_id="ExpedientesConEmpleosRetiradosAutDePortal" vli_description="Listado de expedientes con empleos retirados que están autorizados a los que el usuario autenticado tiene permiso" vli_value_source="UseItemData" vli_codfld="string" vli_enable_caching="0" vli_caching_expiration="0" vli_caching_expiration_mins="0" vli_definition="&lt;List&gt;&lt;Bound&gt;&#xA;select distinct exp_codigo_alternativo, exp_nombres_apellidos&#xA;  from exp.exp_expedientes&#xA;  join exp.emp_empleos on emp_codexp = exp_codigo&#xA;  join eor.plz_plazas on plz_codigo = emp_codplz&#xA;  join acc.ret_retiros on ret_codemp = emp_codigo&#xA; where ret_estado = ''Autorizado'' and emp_estado = ''R'' &#xA;   and not exists (select 1 from acc.rre_reversiones_retiro where rre_codret = ret_codigo and rre_estado != ''Denegado'')&#xA;   and exists(select null from sco.permiso_emp_x_jerarquia_tabla(''$$USER$$'') where emp_codigo = codemp)&#xA;   and convert(date, ret_fecha_retiro) &amp;gt;= (convert(date, (getdate() - cfg.get_parametro_int(''NumeroDiasAnterioresALaActualParaRevertirRetiros''))))&#xA; order by exp_nombres_apellidos&#xA;&lt;/Bound&gt;&lt;/List&gt;" vli_usuario_grabacion="EVA" />
  <lista vli_id="ExpedientesDeEmpleadosNoParientes" vli_description="Lista todos los expedientes de los empleados" vli_prompt="" vli_value_source="UseItemData" vli_codfld="int" vli_enable_caching="0" vli_caching_expiration="0" vli_caching_expiration_mins="1" vli_definition="&lt;List&gt;&lt;Bound&gt;&#xA;    select exp_codigo_alternativo, exp_nombres_apellidos from exp.exp_expedientes where exists(select 1 from exp.emp_empleos where emp_codexp = exp_codigo) and exp_codigo != @codexp and (not exists(select 1 from exp.pae_parientes_empleados where pae_codexp_empleado = exp_codigo and pae_codexp = @codexp) or exp_codigo_alternativo = ''@codpae'') order by exp_nombres_apellidos &#xA;  &lt;/Bound&gt;&lt;/List&gt;" vli_usuario_grabacion="EVA" />
  <lista vli_id="ExpedientesDeEmpleosAEvaluarDeEmpleoEvaluador" vli_description="Lista los expedientes de los empleados a evaluar para un período y empleo evaluador enviado" vli_value_source="UseItemData" vli_codfld="string" vli_enable_caching="0" vli_caching_expiration="0" vli_caching_expiration_mins="0" vli_definition="&lt;List&gt;&lt;Bound&gt;&#xA;  select distinct exp_codigo_alternativo, exp_apellidos_nombres&#xA;    from exp.exp_expedientes&#xA;    join exp.emp_empleos on emp_codexp = exp_codigo&#xA;    join eor.plz_plazas on plz_codigo = emp_codplz&#xA;   where exists(select null from eva.ase_asigna_evaluadores &#xA;                  join exp.emp_empleos e1 on e1.emp_codplz = ase_codplz_evaluador&#xA;                 where ase_codplz = plz_codigo&#xA;                   and emp_codigo = @codemp_evaluador&#xA;                   and ase_codpev = @codpev)&#xA;   and emp_estado = ''A''&#xA;order by exp_apellidos_nombres&#xA;  &lt;/Bound&gt;&lt;/List&gt;" vli_usuario_grabacion="EVA" />
  <lista vli_id="ExpedientesDeEmpleosValuadoresDeGrupoSeleccionado" vli_description="Lista los expedientes de los empleos valuadores correspondientes al grupo seleccionado" vli_value_source="UseItemData" vli_codfld="string" vli_enable_caching="0" vli_caching_expiration="0" vli_caching_expiration_mins="0" vli_definition="&lt;List&gt;&lt;Bound&gt;&#xA;select exp_codigo_alternativo, exp_apellidos_nombres&#xA;  from exp.exp_expedientes exp&#xA;  where exists(select 1 &#xA;                 from exp.emv_emp_valuadores_v emv&#xA;                where exp.exp_codigo = emv.exp_codigo&#xA;                  and emv.grc_codigo = $$CODGRC$$)&#xA;  &lt;/Bound&gt;&lt;/List&gt;" vli_usuario_grabacion="EVA" />
  <lista vli_id="ExpedientesEmpleadosActivosTODOS" vli_description="Muestra todos los expedientes de todos los empleados activos no importando a que empresa pertenecen" vli_prompt="Empleado" vli_value_source="UseSqlExpressionInItemData" vli_codfld="int" vli_enable_caching="0" vli_caching_expiration="0" vli_caching_expiration_mins="0" vli_definition="&lt;List&gt;&lt;Bound&gt;&#xA;select exp_codigo, exp_apellidos_nombres&#xA;  from exp.exp_expedientes&#xA; where exists (select 1 from exp.emp_empleos where exp_codigo = emp_codexp and emp_estado = ''A'')&#xA;order by 2&#xA;&lt;/Bound&gt;&lt;/List&gt;" vli_usuario_grabacion="EVA" />
  <lista vli_id="ExpedientesEmpleosActivosFiltradosPorCiaCdtUni" vli_description="Lista los expedientes con empleos activos filtrados por el codigo de compañía, centros de trabajo y unidad" vli_value_source="UseItemData" vli_codfld="string" vli_enable_caching="0" vli_caching_expiration="0" vli_caching_expiration_mins="0" vli_definition="&lt;List&gt;&lt;Bound&gt;&#xA;    select exp_codigo_alternativo, exp_apellidos_nombres &#xA;      from exp.exp_expedientes&#xA;     where exists(select null&#xA;                    from exp.emp_empleos&#xA;                    join eor.plz_plazas on plz_codigo = emp_codplz&#xA;                    join eor.cia_companias on cia_codigo = plz_codcia&#xA;                    join eor.cdt_centros_de_trabajo on cdt_codigo = plz_codcdt&#xA;                    join eor.uni_unidades on uni_codigo = plz_coduni&#xA;                   where plz_codcia = @codcia &#xA;                     and (isnull(@codcdt, ''-1'') = ''-1'' or plz_codcdt = @codcdt) &#xA;                     and (isnull(@coduni, ''-1'') = ''-1'' or plz_coduni = @coduni)&#xA;                     and emp_estado = ''A''&#xA;                     and emp_codexp = exp_codigo)&#xA;  &lt;/Bound&gt;&lt;/List&gt;" vli_usuario_grabacion="EVA" />
