  <lista vli_id="ExpedientesEmpResponsablesAccionesClimaDeGrupo" vli_description="Muestra los expedientes de los empleos responsables para la accion del plan de acción en clima organizacional" vli_value_source="UseItemData" vli_codfld="int" vli_enable_caching="0" vli_caching_expiration="0" vli_caching_expiration_mins="0" vli_definition="&lt;List&gt;&lt;Bound&gt;&#xA;    select exp_codigo_alternativo, exp_apellidos_nombres&#xA;      from exp.exp_expedientes&#xA;     where exists(select 1 &#xA;                    from exp.emp_empleos &#xA;                    join eor.plz_plazas on plz_codigo = emp_codplz&#xA;                    join eor.cia_companias on cia_codigo = plz_codcia&#xA;                   where emp_codexp = exp_codigo and emp_estado = ''A''&#xA;                     and exists (select null from sco.permiso_empleo_tabla(''$$USER$$'') where emp_codigo = codemp)&#xA;                     and cia_codgrc = @codgrc)&#xA;  order by exp_apellidos_nombres&#xA;  &lt;/Bound&gt;&lt;/List&gt;" vli_usuario_grabacion="EVA" />
