  <lista vli_id="ExpedientesEvaluadoresDeEmpleoEvaluadoYPeriodoEnv" vli_description="Listado de expedientes evaluadores de un empleado y un periodo de evaluacion enviado" vli_value_source="UseItemData" vli_codfld="string" vli_enable_caching="0" vli_caching_expiration="0" vli_caching_expiration_mins="0" vli_definition="&lt;List&gt;&lt;Bound&gt;&#xA;select exp_codigo_alternativo, exp_nombres_apellidos + '' ('' + plz_nombre + '')'' exp_nombres_apellidos&#xA;    from (select distinct ase_codplz_evaluador&#xA;                    from exp.emp_empleos&#xA;                    join exp.exp_expedientes on exp_codigo = emp_codexp &#xA;                    join eva.ase_asigna_evaluadores on ase_codplz = emp_codplz and ase_codpev = @codpev&#xA;                    join eva.dir_direcciones on dir_codigo = ase_coddir&#xA;                where emp_codigo = @codemp_evaluado&#xA;                    and dir_evalua_objetivos = 1  &#xA;                    --and dir_regla_aplica_eor = ''JefeASubAlterno''&#xA;          ) x &#xA;    join eor.plz_plazas on plz_codigo = ase_codplz_evaluador&#xA;    join exp.emp_empleos e on emp_codplz = plz_codigo&#xA;  join exp.exp_expedientes on exp_codigo = emp_codexp&#xA;   where emp_estado = ''A''&#xA;&lt;/Bound&gt;&lt;/List&gt;" vli_usuario_grabacion="EVA" />
