  <lista vli_id="RequisitosDeTipoRequisito" vli_description="Lista los requisitos del tipo de requisito" vli_prompt="" vli_value_source="UseItemData" vli_codfld="int" vli_enable_caching="0" vli_caching_expiration="0" vli_caching_expiration_mins="0" vli_definition="&lt;List&gt;&lt;Bound&gt;&#xA;select  req_codigo, req_descripcion from eor.req_requisitos where req_codtrq = @codtrq and not exists(select 1 from exp.hem_habilidades_empleos where hem_codemp = @codemp and req_codigo = hem_codreq and hem_codigo != @codhem) order by req_descripcion&#xA;  &lt;/Bound&gt;&lt;/List&gt;" vli_usuario_grabacion="EVA" />
