----------------------------------------INICIO IDIOMAS----------------------------------------------------
WITH src AS (

SELECT xt.xc.value('@codigo', 'varchar(50)') AS codigo, xt.xc.value('@nombre', 'varchar(50)') AS descripcion FROM @idoXML.nodes('/idioma/idiomas') AS xt(xc)
) MERGE INTO gen.ido_idiomas dest
	USING src ON src.codigo = dest.ido_codigo
		WHEN NOT MATCHED THEN
			INSERT (ido_codigo, ido_nombre) VALUES (src.codigo, src.descripcion);

----------------------------------------FIN IDIOMAS-------------------------------------------------------
----------------------------------------INICIO TIPOS DE EVENTO--------------------------------------------
WITH src AS (
SELECT xt.xc.value('@descripcion', 'varchar(100)') AS descripcion FROM @myXMLTev.nodes('/tevs/tev') AS xt(xc)
) MERGE INTO cap.tev_tipos_eventos dest
	USING src ON src.descripcion = dest.tev_descripcion AND dest.tev_codgrc = @codgrcTev
		WHEN NOT MATCHED THEN
			INSERT (tev_codgrc, tev_descripcion) VALUES (@codgrcTev, src.descripcion);
----------------------------------------FIN TIPOS DE EVENTO-----------------------------------------------
----------------------------------------INICIO TIPOS DE REQUISITO-----------------------------------------
WITH src AS (
SELECT xt.xc.value('@descripcion', 'varchar(100)') AS descripcion, xt.xc.value('@abreviatura', 'varchar(100)') AS abreviatura, xt.xc.value('@operador', 'varchar(100)') AS operador FROM @myXMLOp.nodes('/operadores/operador') AS xt(xc)
) MERGE INTO eor.trq_tipos_requisitos dest
	USING src ON src.descripcion = dest.trq_descripcion AND src.abreviatura = dest.trq_abreviatura AND src.operador = dest.trq_operador AND dest.trq_codgrc = @codgrcTev
		WHEN NOT MATCHED THEN
			INSERT (trq_descripcion, trq_abreviatura, trq_operador, trq_codgrc) VALUES (src.descripcion, src.abreviatura, src.operador, @codgrcTev);
