	, xt.xc.value('@tpl_hex_override_fecha_fin', 'int') AS tpl_hex_override_fecha_fin
	, xt.xc.value('@tpl_hex_rango_fecha_fin', 'varchar(250)') AS tpl_hex_rango_fecha_fin
	, xt.xc.value('@tpl_asi_rango_fechas_ingreso', 'varchar(250)') AS tpl_asi_rango_fechas_ingreso
