 FROM @tplXML.nodes('/tipos_planilla/tipo_planilla') AS xt(xc)
) MERGE INTO sal.tpl_tipo_planilla dest
	USING src ON src.tpl_descripcion = dest.tpl_descripcion AND dest.tpl_codcia = @codcia
		WHEN MATCHED THEN
			UPDATE SET  tpl_frecuencia = src.tpl_frecuencia
						, tpl_aplicacion = src.tpl_aplicacion
						, dest.tpl_total_periodos = src.tpl_total_periodos
						, tpl_num_periodo = src.tpl_num_periodo
						, tpl_hex_rango_fechas_ingreso = src.tpl_hex_rango_fechas_ingreso
