						, tpl_permite_trans_luego_corte
						, tpl_ignora_aut_luego_corte
						, tpl_genera_proximo_periodo
						, tpl_property_bag_data
						, tpl_usuario_grabacion
					) VALUES (
						@codcia
						, src.tpl_codigo_visual
